class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans = "";
        if(k==1){
            for(int i=0;i<n;i++){
                ans += '9';
            }
        }

        else if(k==2){
            if(n==1) ans ="8";
            else if(n==2) ans = "88";
            else{
                ans += '8';
                for(int i=1;i<n-1;i++){
                    ans += '9';
                }
                ans += '8';
            }
        }

        else if(k==3){
            for(int i=0;i<n;i++) 
                ans += '9';
        }

        else if(k==4){
            if(n<=4){
                for(int i=0;i<n;i++) ans+='8';
            }
            else{
                ans += '8';
                ans += '8';

                for(int i=0;i<n-4;i++) ans += '9';

                ans += '8';
                ans += '8';
            }
        }

        else if(k==5){
            if(n==1) ans ="5";
            else if(n==2) ans = "55";
            else{
                ans += '5';
                for(int i=1;i<n-1;i++){
                    ans += '9';
                }
                ans += '5';
            }
        }

        else if(k==6){
            if(n==1) ans="6";
            else if(n==2) ans="66";
            else{
                ans += '8';
                for(int i=1;i<n-1;i++)
                    ans += '9';
                ans += '8';
                if(n%2==0){
                    int sum=0;
                    for(int i=0;i<n;i++) sum+=(ans[i]-'0');
                    if(sum%6==0) return ans;
                    sum -= 18;
                    if((sum+16)%6==0){
                        ans[(n/2)-1] = '8';
                        ans[n/2] = '8';
                    }
                    else if((sum+14)%6==0){
                        ans[(n/2)-1] = '7';
                        ans[n/2] = '7';
                    }
                    else if((sum+12)%6==0){
                        ans[(n/2)-1] = '6';
                        ans[n/2] = '6';
                    }
                    else if((sum+10)%6==0){
                        ans[(n/2)-1] = '5';
                        ans[n/2] = '5';
                    }
                    else if((sum+8)%6==0){
                        ans[(n/2)-1] = '4';
                        ans[n/2] = '4';
                    }
                    else if((sum+6)%6==0){
                        ans[(n/2)-1] = '3';
                        ans[n/2] = '3';
                    }
                    else if((sum+4)%6==0){
                        ans[(n/2)-1] = '2';
                        ans[n/2] = '2';
                    }
                    else if((sum+2)%6==0){
                        ans[(n/2)-1] = '1';
                        ans[n/2] = '1';
                    }
                }
                else{
                    int sum=0;
                    for(int i=0;i<n;i++) sum+=(ans[i]-'0');
                    if(sum%6==0) return ans;
                    sum -= 9;
                    if((sum+8)%6==0){

                        ans[n/2] = '8';
                    }
                    else if((sum+7)%6==0){

                        ans[n/2] = '7';
                    }
                    else if((sum+6)%6==0){

                        ans[n/2] = '6';
                    }
                    else if((sum+5)%6==0){

                        ans[n/2] = '5';
                    }
                    else if((sum+4)%6==0){

                        ans[n/2] = '4';
                    }
                    else if((sum+3)%6==0){

                        ans[n/2] = '3';
                    }
                    else if((sum+2)%6==0){

                        ans[n/2] = '2';
                    }
                    else if((sum+1)%6==0){
                        ans[n/2] = '1';
                    }

                }
            }
        }

        else if(k==7){
            string s="";
            if(n==1){
                return "7";
            }
            else if(n==2){
                return "77";
            }
            for(int i=0;i<n;i++){
                s+='9';
            }
            ans=s;
            for(char x='1';x<='9';x++){
                if(n%2==0){
                    s[(n-1)/2]=x;
                    s[n/2]=x;

                    vector<int> v={1,3,2,6,4,5};
                    int diff=0;
                    int j=0;
                    for(int i=n-1;i>=0;i--){
                        int val= (s[i]-'0')*v[j];
                        j++;

                        j%=6;
                        val%=7;
                        diff+=val;
                        diff%=7;
                    }
                    if(diff==0){
                        ans=s;
                    }
                }
                else{
                    // s[(n-1)/2]=x;
                    s[n/2]=x;

                    vector<int> v={1,3,2,6,4,5};
                    int diff=0;
                    int j=0;
                    for(int i=n-1;i>=0;i--){
                        int val= (s[i]-'0')*v[j];
                        j++;
                        j%=6;
                        val%=7;
                        diff+=val;
                        diff%=7;
                    }
                    if(diff==0){
                        ans=s;
                    }
                }
            }


        }

        else if(k==8){
            if(n<=6){
                for(int i=0;i<n;i++) ans+='8';
            }
            else{
                ans += '8';
                ans += '8';
                ans += '8';
                for(int i=0;i<n-6;i++) ans += '9';
                ans += '8';
                ans += '8';
                ans += '8';
            }
        }

        else if(k==9){
            for(int i=0;i<n;i++) ans+='9';
        }

        return ans;
    }
};
