class Solution {
public:
    bool isDivisibleBy7(const std::string& number) {
    int remainder = 0;
    for (char digit : number) {
        int currentDigit = digit - '0';
        remainder = remainder * 10 + currentDigit;

        if(remainder<7) continue;
        remainder %= 7;
    }
    return (remainder == 0);
}
    string largestPalindrome(int n, int k) {
        string ans = "";

        for(int i=0;i<n;i++){
            ans.push_back('9');
        }
        if(k==1 or k==3 or k==9) return ans;
        if(k==2){
            ans[0]='8';
            ans[n-1]='8';
            return ans;
        }
        if(k==4){
            if(n==1) return "8";
            ans[0]='8';
            ans[1]='8';
            ans[n-2]='8';
            ans[n-1]='8';
            return ans;
        }
        if(k==5){
            ans[0]=ans[n-1]='5';
            return ans;
        }
        if(k==6){
            if(n==1) return "6";
            if(n==2) return "66";
            if(n==3) return "888";
            if(n==4) return "8778";
            if(n==5) return "89898";
            if(n%2==0){
                ans[0]='8';
                ans[n-1]='8';
                int mid = n/2-1;
                ans[mid]=ans[mid+1]='7';
            }
            else{
                ans[0]='8';
                ans[n-1]='8';
                int mid = n/2;
                ans[mid]='8';
            }
            return ans;
        }
        if(k==8){
            if(n>=3){
                ans[0]='8';
            ans[1]='8';
                ans[2]='8';
            ans[n-2]='8';
            ans[n-1]='8';
                ans[n-3]='8';
            }
            if(n==2) return "88";
            if(n==1) return "8";
            return ans;

        }
        if(k==7){
            if(n%2){
                int mid = n/2;
                for(char c = '9';c>='0';c--){
                    ans[mid]=c;
                    bool f = isDivisibleBy7(ans);
                    // cout<<ans<<" "<<f<<endl;
                    if(f)return ans;
                }
            }
            else{
                int mid = n/2-1;
                for(char c = '9';c>='0';c--){
                    ans[mid]=c;
                    ans[mid+1]=c;
                    if(isDivisibleBy7(ans))return ans;
                }
            }
        }

        return ans;
    }
};
