class Solution {
public:
    bool isPal(string s)
    {
        int i=0, j=s.size()-1;
        while(i<j)
        {
            if(s[i]!=s[j])
                return 0;
            i++;
            j--;
        }

        return 1;
    }
    bool isDiv(string s, int k)
    {
        int mod=0;
        for(char ch:s)
        {
            mod=mod*10+(ch-'0');
            mod%=k;
        }

        return mod==0;
    }
    string f(int n, int k)
    {
        string ans;
        int l=pow(10, n-1);
        int r=pow(10, n);
        for(int i=l; i<r; i++)
        {
            if(i%k==0 && isPal(to_string(i)))
            {
                ans=to_string(i);
            }
        }

        return ans;
    }
    string largestPalindrome(int n, int k) {
        if(n<=4)
            return f(n, k);

        if(k==1 || k==9 || k==3)
            return string(n, '9');
        else if(k==2)
        {
            string s(n, '9');
            s[0]='8';
            s[n-1]='8';
            return s;
        }
        else if(k==4)
        {
            string s(n, '9');
            s[0]=s[1]='8';
            s[n-1]=s[n-2]='8';
            return s;
        }
        else if(k==5)
        {
            string s(n, '9');
            s[0]=s[n-1]='5';
            return s;
        }
        else if(k==6)
        {
            string s(n, '9');
            s[0]=s[n-1]='8';

            if(n%2)
                s[n/2]='8';
            else
                s[n/2]=s[n/2-1]='7';
            return s;
        }
        else if(k==8)
        {
            string s(n, '9');
            s[0]=s[n-1]='8';
            s[1]=s[n-2]='8';
            s[2]=s[n-3]='8';

            return s;
        }
        else
        {
            string s(n, '9');

            if(n%2)
            {
                for(char digit='9'; digit>='0'; digit--)
                {
                    s[n/2]=digit;
                    if(isPal(s) && isDiv(s, 7))
                        return s;
                }

            }
            else
            {
                for(char digit='9'; digit>='0'; digit--)
                {
                    s[n/2]=digit;
                    s[n/2-1]=digit;
                    if(isPal(s) && isDiv(s, 7))
                        return s;
                }
            }
        }

        return "";
    }
};
