bool can(string s)
{
    int x=0;
    for(int i=0;i<s.length();i++)
    {
        x=x*10+s[i]-'0';
        x%=7;
    }
    return x==0;
}
class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans="";
        if(k==1||k==3||k==9)
        {
            for(int i=0;i<n;i++)
                ans+='9';
        }
        else if(k==5)
        {
            for(int i=0;i<n;i++)
                    ans+='9';
            ans[0]=ans[n-1]='5';
        }
        else if(k==6)
        {
            if(n==1)
                ans="6";
            else if(n==2)
                ans="66";
            else
            {
                for(int i=0;i<n;i++)
                    ans+='9';
                if(n&1)
                    ans[0]=ans[n-1]=ans[n/2]='8';
                else
                {
                    ans[0]=ans[n-1]='8';
                    ans[n/2]=ans[n/2-1]='7';
                }

            }
        }
        else if(k==2)
        {
            for(int i=0;i<n;i++)
                    ans+='9';
            ans[0]=ans[n-1]='8';
        }
        else if(k==4)
        {
            for(int i=0;i<n;i++)
                    ans+='9';
            ans[0]=ans[n-1]='8';
            if(1<n) ans[1]=ans[n-2]='8';
        }
        else if(k==8)
        {
            for(int i=0;i<n;i++)
                    ans+='9';
            ans[0]=ans[n-1]='8';
            if(1<n) ans[1]=ans[n-2]='8';
            if(2<n) ans[2]=ans[n-3]='8';
        }
        else
        {
            if(n==1)
                ans="7";
            else if(n==2)
                ans="77";
            else
            {
                for(int i=0;i<n;i++)
                    ans+='9';
                for(int i=9;i>=0;i--)
                {
                    if(n&1)
                        ans[n/2]=i+'0';
                    else
                        ans[n/2]=ans[n/2-1]=i+'0';
                    if(can(ans))
                        break;
                }
            }
        }
        return ans;
    }
};
