class Solution {
public:
    string largestPalindrome(int n, int k) {
        string all9(n,'9');
        string ans2(n,'9');
        string ans5(n,'9');
        ans2[0]='8';
        ans2.back()='8';
        ans5[0]='5';
        ans5.back()='5';
        if(k==1||k==3||k==9) return all9;
        else if(k==2) return ans2;
        else if(k==5) return ans5;
        else if(k==4){
            if(n<=4) return string(n,'8');
            else{
                string res(n,'9');
                res[0]='8';
                res[1]='8';
                res[n-2]='8';
                res[n-1]='8';
                return res;
            }
        }else if(k==8){
            if(n<=6) return string(n,'8');
            else{
                string res(n,'9');
                res[0]='8';
                res[1]='8';
                res[2]='8';
                res[n-3]='8';
                res[n-2]='8';
                res[n-1]='8';
                return res;
            }
        }else if(k==6){
            if(n<=2) return string(n,'6');
            else if(n%2==0){
                string res=ans2;
                int sum=16+(n-2)*9;
                sum%=6;
                sum/=2;
                res[n/2-1]-=sum;
                res[n/2]-=sum;
                return res;
            }else{
                string res=ans2;
                int sum=16+(n-2)*9;
                sum%=3;
                res[n/2]-=sum;
                return res;
            }
        }
        else {
            if(n<=2) return string(n,'7');
            if(n%2==1){
                vector<int> mid={5,7,4,6,4,7};
                string res(n,'9');
                res[n/2]='0'+mid[((n-3)/2)%6];
                return res;
            }else{
                vector<int> mid={7,9,4,4,9,7};
                string res(n,'9');
                res[n/2-1]='0'+mid[((n-3)/2)%6];
                res[n/2]='0'+mid[((n-4)/2)%6];
                return res;
            }
        }
    }
};
