class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def f(s):
            rem=0
            for  i in range(n):
                rem=(rem*10+ord(s[i])-ord('0'))%7
            return rem==0
        if k==1 or k==3 or k==9:
            return '9'*n
        if k==2:
            return '8'+'9'*(n-2)+'8' if n>=2 else '8'
        if k==4:
            return '88'+'9'*(n-4)+'88' if n>=4 else '8'*n
        if k==5:
            return '5'+'9'*(n-2)+'5' if n>=2 else '5'
        if k==6:
            if n<=2:
                return '6'*n
            return '8'+'9'*((n-3)//2)+'8'+'9'*((n-3)//2)+'8' if n&1 else '8'+'9'*((n-4)//2)+'77'+'9'*((n-4)//2)+'8'
        if k==7:
            for i in range(9,-1,-1):
                x=2-(n&1)
                s='9'*((n-x)//2)+chr(i+ord('0'))*(x)+'9'*((n-x)//2)
                if f(s):
                    return s
        if k==8:
            return '888'+'9'*(n-6)+'888' if n>=6 else '8'*n
