class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            ans = ['9'] * n
        elif k == 2:
            ans = ['9'] * n
            ans[0] = '8'
            ans[-1] = '8'
        elif k == 4:
            ans = ['9'] * n
            ans[0] = '8'
            ans[-1] = '8'
            if n >= 2:
                ans[1] = '8'
                ans[-2] = '8'
        elif k == 5:
            ans = ['9'] * n
            ans[0] = '5'
            ans[-1] = '5'
        elif k == 6:
            ans = ['9'] * n
            ans[0] = '8'
            ans[-1] = '8'
            if n == 1:
                ans[0] = '6'
            elif n == 2:
                ans[0] = '6'
                ans[1] = '6'
            elif n % 2 == 0:
                ans[n // 2 - 1] = '7'
                ans[n // 2] = '7'
            elif n % 2 == 1:
                ans[n // 2] = '8'
        elif k == 8:
            ans = ['9'] * n
            ans[0] = '8'
            ans[-1] = '8'
            if n >= 3:
                ans[1] = '8'
                ans[-2] = '8'
                ans[2] = '8'
                ans[-3] = '8'
        elif k == 7:
            ans = ['9'] * n
            for mid in range(9, 0, -1):
                if n % 2 == 0:
                    ans[n // 2 - 1] = str(mid)
                    ans[n // 2] = str(mid)
                else:
                    ans[n // 2] = str(mid)

                ten_base = 1
                tmp = 0
                for i in range(n):
                    tmp = (tmp + ten_base * int(ans[i])) % 7
                    ten_base = ten_base * 10 % 7
                # print(ans, tmp)
                if tmp == 0:
                    break

        return ''.join(ans)
