class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:


        def find(n):
            def dfs(n, t, q):
                # "99999"
                # 10 ^ 5 - 1
                if len(q) == t:
                    m = (pow(10, n, 7) - 1 - (int("9" * t) - int(q)) * (pow(10, (n - t) // 2, 7))) 
                    m = m % 7
                    if m < 0:
                        m += 7
                    if m % 7 == 0:
                        return True, "9" * ((n - t) // 2) + q + "9" * ((n - t) // 2)
                    else:
                        return False, ""
                    return True, ""
                else:
                    for i in range(9, -1, -1):
                        if len(q) == 0 and t % 2 == 1:
                            nq = str(i)
                        else:
                            nq = str(i) + str(q) + str(i)
                        f, ret = dfs(n, t, nq)
                        if f:
                            return f, ret
                    return False, ""

            t = 2
            if n % 2 == 1:
                t = 1
            else:
                t = 2
            while True:
                if t > n:
                    break
                f, ret = dfs(n, t, "")
                if f:
                    return ret
                else:
                    t += 2

            return "7" * n





        if k == 1:
            return '9' * n
