from typing import List


class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        s = ''
        if k  in [1,3,9]:
            for i in range(n):
                s+='9'
            return s
        if k ==2:
            s+='8'
            for i in range(n-2):
                s+='9'
            if n>1:
                s+='8'
            return s
        if k == 5:
            s += '5'
            for i in range(n - 2):
                s += '9'
            if n>1:
                s += '5'
            return s
        if k ==4:
            if n ==1:
                return '8'
            if n ==2:
                return '88'
            if n ==3:
                return '888'
            if n ==4:
                return '8888'

            s += '88'
            for i in range(n - 4):
                s += '9'

            s += '88'
            return s
        if k ==6:

            if n ==1:
                return '6'
            if n ==2:
                return '66'
            if n ==3:
                return '888'
            if n ==4:
                return '8778'
            if n%2==0:
                s+='8'
                for i in range((n-4)//2):
                    s+='9'
                s+='77'
                for i in range((n-4)//2):
                    s+='9'
                s+='8'
            else:
                s += '8'
                for i in range((n - 3) // 2):
                    s += '9'
                s += '8'
                for i in range((n - 3) // 2):
                    s += '9'
                s += '8'
            return s


        if k ==8:
            #
            if n ==1:
                return '8'
            if n ==2:
                return '88'
            if n == 3:
                return '888'
            if n ==4:
                return '8888'
            if n ==5:
                return '88888'
            if n ==6:
                return '888888'
            s+='888'
            for i in range(n-6):
                s+='9'
            s += '888'
            return s
        if k ==7:
            if n ==1:
                return '7'

            # 12
            a = ['7', '77', '5', '77', '7', '', '4', '44', '6', '44', '4', '']
            choice = a[(n%12)-1]
            digitsleft = n-len(choice)
            x = ''
            for i in range(digitsleft//2):
                x+='9'
            s=x+choice+x
            return s
