class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k in [1,3,9]:
            ret = ''
            for _ in range(n):
                ret += '9'
            return ret
        elif k == 5:
            if n == 1:
                return '5'
            else:
                ret = '5'
                for _ in range(1, n-1):
                    ret += '9'
                ret += '5'
            return ret
        elif k == 2:
            if n == 1:
                return '8'
            else:
                ret = '8'
                for _ in range(1, n-1):
                    ret += '9'
                ret += '8'
            return ret
        elif k == 4:
            if n <= 4:
                ret = ''
                for _ in range(n):
                    ret += '8'
                return ret
            else:
                ret = '88'
                for _ in range(2, n-2):
                    ret += '9'
                ret += '88'
            return ret
        elif k == 8:
            if n <= 6:
                ret = ''
                for _ in range(n):
                    ret += '8'
                return ret
            else:
                ret = '888'
                for _ in range(3, n-3):
                    ret += '9'
                ret += '888'
            return ret
        elif k == 6:
            if n <= 4:
                ret = ['6', '66', '888', '8778']
                return ret[n-1]
            else:
                if n % 2 == 1:
                    ret = '8'
                    for _ in range((n-3)//2):
                        ret += '9'
                    ret += '8'
                    for _ in range((n-3)//2):
                        ret += '9'
                    ret += '8'
                else:
                    ret = '8'
                    for _ in range((n-4)//2):
                        ret += '9'
                    ret += '77'
                    for _ in range((n-4)//2):
                        ret += '9'
                    ret += '8'
                return ret
        elif k == 7:                
            ret7 = ['7', '77', '959', '9779', '99799', '999999',
                    '9994999', '99944999',
                    '999969999', '9999449999',
                    '99999499999','999999999999']
            if n <= 12:
                return ret7[n-1]
            elif n % 6 == 0:
                ret = ''
                for _ in range(n):
                    ret += '9'
                return ret
            else:
                mid = n % 12
                ret = ''
                for _ in range((n-mid)//2):
                    ret += '9'
                ret += ret7[mid-1]
                for _ in range((n-mid)//2):
                    ret += '9'
                return ret
