class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans;
        if(k==1)
        {
            for(int i =0; i < n; i++) ans.push_back('9');
        }
        if(k==2)
        {
            if(n == 1) ans = string("8");
            else
            {
                ans.push_back('8');
                for(int i =1; i < n-1; i++) ans.push_back('9');
                ans.push_back('8');
            }
        }
        if(k==3)
        {
            for(int i =0; i < n; i++) ans.push_back('9');
        }
        if(k==4)
        {
            if(n == 1) ans = string("8");
            else if(n == 2) ans = string("88");
            else if(n == 3) ans = string("888");
            else 
            {
                ans += string("88");
                for(int i =2; i < n-2; i++) ans.push_back('9');
                ans += string("88");
            }
        }
        if(k==5)
        {
            if(n == 1) ans.push_back('5');
            else
            {
                ans.push_back('5');
                for(int i =1; i < n-1; i++) ans.push_back('9');
                ans.push_back('5');
            }
        }
        if(k==6)
        {
            if(n == 1) ans = string("6");
            else if(n == 2) ans = string("66");
            else if(n == 3) ans = string("888");
            else if(n == 4) ans = string("8778");
            else {
                ans += string("8");
                for(int i=0; i < (n-3)/2;i ++) ans += string("9");
                if(n&1) ans += string("8");
                else ans += string("77");
                for(int i=0; i < (n-3)/2;i ++) ans += string("9");
                ans += string("8");
            }
        }
        if(k==7)
        {
            int m = n / 12;
            for(int i=0; i< m;i++) ans += string("999999");
            int l= n%12;
            if(l == 1) ans += string("7");
            if(l == 2) ans += string("77");
            if(l == 3) ans += string("959");
            if(l == 4) ans += string("9779");
            if(l == 5) ans += string("99799");
            if(l == 6) ans += string("999999");
            if(l == 7) ans += string("9994999");
            if(l == 8) ans += string("99944999");
            if(l == 9) ans += string("999969999");
            if(l == 10) ans += string("9999449999");
            if(l == 11) ans += string("99999499999");
            // if(l == 0) ans += string("999999999999");
            for(int i=0; i< m;i++) ans += string("999999");
        }
        if(k==8)
        {
            if(n <= 6)
                for(int i =0; i < n; i++) ans.push_back('8');
            else
            {
                ans += string("888");
                for(int i =0; i < (n-6); i++) ans.push_back('9');
                ans += string("888");
            }
        }
        if(k==9)
        {
            for(int i =0; i < n; i++) ans.push_back('9');
        }

        return ans;
    }
};
