class Solution {
public:
    bool isDivisibleByK(const string& numStr, int k) {
        int remainder = 0;
        for (char digit : numStr) {
            remainder = (remainder * 10 + (digit - '0')) % k;
        }
        return remainder == 0;
    }
    string largestPalindrome(int n, int k) {
        string str = "";
        if(k == 1)
        {
            //char c = char('0' + k); 
            for(int i=0;i<n;i++)str += '9';
            return str;
        }
        else if(k == 2)
        {
            if(n == 1)return "8";
            str += '8';
            for(int i=0;i<n-2;i++)str += '9';
            str += '8';
            return str;
        }
        else if(k == 4)
        {
            if(n == 1)return "8";
            else if(n == 2)return "88";
            else if(n == 3)return "888";
            else if(n == 4)return "8888";
            else
            {
                str += "88";
                for(int i=0;i<n-4;i++)str += '9';
                str += "88";
                return str;
            }
        }
        else if(k == 8)
        {
            if( n <= 6)
            {
                for(int i=0;i<n;i++)str += '8';
            }
            else
            {
                str += "888";
                for(int i=0;i<n-6;i++)str += '9';
                str += "888";
            }
            return str;
        }
        else if(k == 5)
        {
            if(n == 1)return "5";
            str += '5';
            for(int i=0;i<n-2;i++)str += '9';
            str += '5';
            return str;
        }
        else
        {
            if(n == 1)
            {
                if(k == 3)return "9";
                if(k == 6)return "6";
                if(k == 7)return "7";
                if(k == 9)return "9";
            }
            if(n == 2)
            {
                if(k == 3)return "99";
                if(k == 6)return "66";
                if(k == 7)return "77";
                if(k == 9)return "99";
            }
            if(n % 2 == 1)
            {
                if(k != 6)
                {
                    string s1 = "";
                    for(int i=0;i<n/2;i++)s1 += '9';
                    for(char c = '9';c >= '0';c--)
                    {
                        string str = s1 + c + s1;
                        if(isDivisibleByK(str, k))return str;
                    } 
                }
                else
                {
                    string s1 = "";
                    s1 += '8';
                    string s2 = "";
                    for(int i=0;i<n/2-1;i++)s2 += '9';
                    for(char c = '9';c >= '0';c--)
                    {
                        string str = s1 + s2 + c + s2 + s1;
                        if(isDivisibleByK(str, k))return str;
                    }

                }
            }
            else
            {
                if(k != 6)
                {
                    string s1 = "";
                    for(int i=0;i<n/2 - 1;i++)s1 += '9';
                    for(char c = '9';c >= '0';c--)
                    {
                        string str = s1 + c + c + s1;
                        if(isDivisibleByK(str, k))return str;
                    } 
                }
                else
                {
                    string s1 = "";
                    s1 += '8';
                    string s2 = "";
                    for(int i=0;i<n/2-2;i++)s2 += '9';
                    for(char c = '9';c >= '0';c--)
                    {
                        string str = s1 + s2 + c + c + s2 + s1;
                        if(isDivisibleByK(str, k))return str;
                    }

                }
            }
        }
        return "0";
    }
};
