class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            string ans = "";
            for (int i = 0; i < n; ++i) {
                ans += "9";
            }
            return ans;
        }
        else if (k == 2) {
            if (n == 1) {
                return "8";
            }
            else {
                string ans = "";
                for (int i = 0; i < n; ++i) {
                    if (i == 0 || i == n - 1) {
                        ans += "8";
                    }
                    else {
                        ans += "9";
                    }
                }
                return ans;
            }
        }
        else if (k == 4) {
            string ans = "";
            for (int i = 0; i < n; ++i) {
                if (i == 0 || i == n - 1 || i == 1 || i == n - 2) {
                    ans += "8";
                }
                else {
                    ans += "9";
                }
            }
            return ans;
        }
        else if (k == 5) {
            if (n == 1) {
                return "5";
            }
            else {
                string ans = "";
                for (int i = 0; i < n; ++i) {
                    if (i == 0 || i == n - 1) {
                        ans += "5";
                    }
                    else {
                        ans += "9";
                    }
                }
                return ans;
            }
        }
        else if (k == 6) {
            if (n == 1) {
                return "6";
            }
            else if (n == 2) {
                return "66";
            }
            else {
                int l = n;
                vector<char> ans(n);
                int cur = 0;
                for (int i = 0; i < (n + 1) / 2; ++i) {
                    if (i == 0) {
                        ans[i] = '8'; ans[(n - 1) - i] = '8';
                        l -= 2;
                        cur++;
                        cur %= 3;
                    }
                    else if (l == 1) {
                        ans[i] = '8';
                    }
                    else if (l == 2) {
                        ans[i] = '7'; ans[(n - 1) - i] = '7';
                    }
                    else {
                        ans[i] = '9'; ans[(n - 1) - i] = '9';
                        l -= 2;
                    }
                }
                string v = "";
                for (char i : ans) {
                    v += i;
                }
                return v;
            }
        }
        else if (k == 8) {
            string ans = "";
            for (int i = 0; i < n; ++i) {
                if (i == 0 || i == n - 1 || i == 1 || i == n - 2 || i == 2 || i == n - 3) {
                    ans += "8";
                }
                else {
                    ans += "9";
                }
            }
            return ans;
        }
        else { // k == 7
            assert(k == 7);
            if (n == 1) {
                return "7";
            }
            else if (n == 2) {
                return "77";
            }
            else {
                int l = n;
                vector<char> ans(n);
                int cur = 0;
                int c10 = 1;
                int c = 0;
                for (int i = 0; i < n; ++i) {
                    int p = (n - 1) - i;

                    if (n % 2 == 1 && i == (n / 2)) {
                        ans[i] = '_';
                        c += c10;
                    }
                    else if (n % 2 == 0 && (i == (n / 2) || i == (n / 2 - 1))) {
                        ans[i] = '_';
                        c += c10;
                    }
                    else {
                        ans[i] = '9';
                        cur += (9 * c10);
                        cur %= 7;
                    }
                    c10 *= 3;
                    c10 %= 7;
                }

                if (n % 2 == 1) {
                    for (int i = 9; i >= 0; i--) {
                        if ((cur + c * i) % 7 == 0) {
                            ans[n/2] = char(i + '0');
                            break;
                        }
                    }
                }
                else {
                    for (int i = 9; i >= 0; i--) {
                        if ((cur + c * i) % 7 == 0) {
                            ans[n/2] = char(i + '0');
                            ans[n/2 - 1] = char(i + '0');
                            break;
                        }
                    }
                }

                string v = "";
                for (char i : ans) {
                    v += i;
                }
                return v;
            }
        }
    }
};
