class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        }
        if (k == 2) {
            string res = "8";
            if (n > 1) {
                if ((n - 2) > 0) {
                    res += string(n-2, '9');
                }
                res += "8";
            }
            return res;
        }

        if (k == 4) {
            if (n < 5) {
                return string(n, '8');
            }
            string res = "88";
            res += string(n-4, '9');
            res += "88";
            return res;
        }
        if (k == 5) {
            string res = "5";
            if (n > 1) {
                if ((n - 2) > 0) {
                    res += string(n-2, '9');
                }
                res += "5";
            }
            return res;
        }

        if (k == 6) {
            if (n < 3) {
                return string(n, '6');
            } else if (n == 3) {
                return "888";
            }

            string res = "8";
            if (n % 2 == 0) {
                res += string((n-4)/2, '9');
                res += "77";
                res += string((n-4)/2, '9');
                res += "8";
            } else {
                res += string((n-3)/2, '9');
                res += "8";
                res += string((n-3)/2, '9');
                res += "8";
            }
            return res;
        }

        if (k == 8) {
            if (n < 7) {
                return string(n, '8');
            }
            string res = string(3,'8');
            res += string(n - 6,'9');
            res += string(3,'8');
            return res;
        }

        if (k == 7) {
            if (n % 6 == 0) {
                return string(n, '9');
            }

            int m = n % 12;
            if (m < 6) {
                if (m == 1 || m == 5) {
                    string res = string((n - 1) / 2, '9');
                    res += "7";
                    res += string((n - 1) / 2, '9');
                    return res;
                } else if (m == 2 || m == 4) {
                    string res = string((n - 2) / 2, '9');
                    res += "77";
                    res += string((n - 2) / 2, '9');
                    return res;
                } else {
                    // m == 3
                    string res = string((n - 1) / 2, '9');
                    res += "5";
                    res += string((n - 1) / 2, '9');
                    return res;
                }
            } else {
                if (m == 7 || m == 11) {
                    string res = string((n - 1) / 2, '9');
                    res += "4";
                    res += string((n - 1) / 2, '9');
                    return res;
                } else if (m == 8 || m == 10) {
                    string res = string((n - 2) / 2, '9');
                    res += "44";
                    res += string((n - 2) / 2, '9');
                    return res;
                } else {
                    // m == 9 
                    string res = string((n - 1) / 2, '9');
                    res += "6";
                    res += string((n - 1) / 2, '9');
                    return res;
                }
            }
        }
        return "0";
    }
};
