class Solution 
{
public:
    bool isDivisibleBy7(const string &strNum) {
        long long remainder = 0;
        for (char digit : strNum) {
            remainder = (remainder * 10 + (digit - '0')) % 7;
        }
        return remainder == 0;
    }
    string handle7(int n)
    {
        if(n==1) return "7";
        if(n==2) return "77";
        if((n&1)==0)
        {
            string s((n-2)/2,'9');
            for(int i=9;i>=0;i--)
            {
                string t=to_string(i)+to_string(i);
                if(isDivisibleBy7(s+t+s))
                {
                    return s+t+s;
                }
            }
        }
        else
        {
            string s((n-3)/2,'9');
            for(int i=9;i>=0;i--)
            {
                for(int j=9;j>=0;j--)
                {
                    string t=to_string(i)+to_string(j)+to_string(i);
                    if(isDivisibleBy7(s+t+s))
                    {
                        return s+t+s;
                    }
                }
            }
        }
        return "";
    }
    string largestPalindrome(int n, int k) 
    {
        switch(k)
        {
            case 1:
                return string(n,'9'); 
            case 2:
                if(n==1) return "8";
                return "8" + string(n-2,'9') + "8";
            case 3:
                return string(n,'9'); 
            case 4:
                if(n<=4) return string(n,'8');
                return "88" + string(n-4,'9') + "88";
            case 5:
                if(n==1) return "5";
                if(n==2) return "55";
                return "5" + string(n-2,'9') + "5";
            case 6:
                if(n==1) return "6";
                if(n==2) return "66";
                if(n%2==0)
                {
                    string s((n-4)/2, '9');   
                    return "8"+s+"77"+s+"8";
                }
                return "8" + string((n-3)/2,'9') + "8" + string((n-3)/2,'9') + "8";
            case 7:
                return handle7(n);
            case 8:
                if(n<=6) return string(n,'8');  
                // 1000 992 984 976 968
                return "888" + string(n-6,'9') + "888"; 
            case 9:
                return string(n,'9');
        }     
        return "";
    }
};
