class Solution {
public:
    string ones(int n)
    {
        return string(n,'9');
    }
    string twos(int n)
    {
        string cur(n,'9');
        cur.front()='8';
        cur.back()='8';
        return cur;
    }
    string three(int n)
    {
        return ones(n);
    }
    string four(int n)
    {
        if(n<=4) return string(n,'8');
        string cur=string(2,'8')+string(n-4,'9')+string(2,'8');
        return cur;
    }
    string five(int n){
        string cur(n,'9');
        cur.front()='5';
        cur.back()='5';
        return cur;
    }
    bool divbysix(string s)
    {
        int cur=0;
        for(auto it:s)
        {
            cur+=it-'0';
        }
        if(cur%3==0 and (s.back()-'0')%2==0) return true;
        return false;
    }
    string six(int n)
    {
        if(n<=2) return string(n,'6');
        string cur(n,'9');
        cur.front()=cur.back()='8';
        if(n%2==0)
        {
            int mid=n/2-1;
            while(!divbysix(cur))
            {
                cur[mid]--;
                cur[mid+1]--;
            }
            return cur;
        }
        else
        {
            int mid=n/2;
            while(!divbysix(cur))
            {
                cur[mid]--;
            }
            return cur;
        }
    }
    bool divbyseven(string cur)
    {
        int remainder = 0;
        for (char digit : cur) {
            remainder = (remainder * 10 + (digit - '0')) % 7;
        }
        return remainder == 0;
    }
    string seven(int n)
    {
        if(n<3) return string(n,'7');
        string cur(n,'9');
        if(n%2==0)
        {
            int mid=n/2-1;
            while(!divbyseven(cur))
            {
                cur[mid]--;
                cur[mid+1]--;
            }
            return cur;
        }
        else
        {
            int mid=n/2;
            while(!divbyseven(cur))
            {
                cur[mid]--;
            }
            return cur;
        }
    }
    string eight(int n)
    {
        if(n<=6) return string(n,'8');
        return string(3,'8')+string(n-6,'9')+string(3,'8');
    }
    string nine(int n)
    {
        return three(n);
    }
    string largestPalindrome(int n, int k) {
        switch(k)
        {
            case 1:return ones(n);
            case 2:return twos(n);
            case 3: return three(n);
            case 4:return four(n);
            case 5:return five(n);
            case 6:return six(n);
            case 7:return seven(n);
            case 8:return eight(n);
            case 9:return nine(n);
        }
        return "";
    }
};
