class Solution {
    public String largestPalindrome(int n, int k) {
        return switch(k) {
            case 1 -> full(n);
            case 3 -> full(n);
            case 9 -> full(n);
            case 2 -> two(n);
            case 4 -> four(n);
            case 8 -> eight(n);
            case 5 -> five(n);
            case 6 -> six(n);
            case 7 -> seven(n);
            default -> "";
        };
    }

    String full(int n) {
        return "9".repeat(n);
    }

    String two(int n) {
        if (n == 1) {
            return "8";
        } else if (n == 2) {
            return "88";
        } else {
            return "8" + full(n - 2) + "8";
        }
    }

    String four(int n) {
        if (n == 1) {
            return "8";
        } else if (n == 2) {
            return "88";
        } else if (n == 3) {
            return "888";
        } else if (n == 4) {
            return "8888";
        } else {
            return "88" + full(n - 4) + "88";
        }
    }

    String eight(int n) {
        if (n <= 6) {
            return "8".repeat(n);
        } else {
            return "888" + full(n - 6) + "888";
        }
    }

    String five(int n) {
        if (n <= 2) {
            return "5".repeat(n);
        } else {
            return "5" + full(n - 2) + "5";
        }
    }

    String six(int n) {
        if (n % 2 == 0) {
            if (n == 2) {
                return "6".repeat(n);
            } else {
                return "8" + full((n - 4) / 2) + "77" + full((n - 4) / 2) + "8";
            }
        } else {
            if (n == 1) {
                return "6";
            } else {
                return "8" + full((n - 3) / 2) + "8" + full((n - 3) / 2) + "8";
            }
        }
    }

    String seven(int n) {
        if (n % 6 == 0) {
            return full(n);
        }

        int rem = n % 12;

        switch (rem) {
            case 1 -> {
                return full((n - 1) / 2) + "7" + full((n - 1) / 2);
            }
            case 5 -> {return full((n - 1) / 2) + "7" + full((n - 1) / 2);}
            case 3 -> {return full((n - 1) / 2) + "5" + full((n - 1) / 2);}
            case 7 -> {return full((n - 1) / 2) + "4" + full((n - 1) / 2);}
            case 11 ->{ return full((n - 1) / 2) + "4" + full((n - 1) / 2);}
            case 9 -> {return full((n - 1) / 2) + "6" + full((n - 1) / 2);}
            case 2 -> {return full((n - 2) / 2) + "77" + full((n - 2) / 2);}
            case 4 -> {return full((n - 2) / 2) + "77" + full((n - 2) / 2);}
            case 8 -> {return full((n - 2) / 2) + "44" + full((n - 2) / 2);}
            case 10 ->{ return full((n - 2) / 2) + "44" + full((n - 2) / 2);}
        }

        return "";
    }
}
