class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        import sys
        sys.set_int_max_str_digits(0)
        if k == 1 or k == 3 or k == 9:
            return '9' * n
        elif k == 2:
            if n == 1:
                return '8'
            return '8' + '9' * (n-2) + '8'
        elif k == 4:
            if n <= 4:
                return '8' * n
            else:
                return '88' + '9' * (n-4) + '88'
        elif k == 5:
            if n == 1:
                return '5'
            else:
                return '5' + '9' * (n-2) + '5'
        elif k == 6:
            if n == 1:
                return '6'
            elif n == 2:
                return '66'
            elif n == 3:
                return '888'
            elif n % 2 == 0:
                return '8' + '9' * ((n-4)//2) + '77' + '9' * ((n-4)//2) + '8'
            else:
                return '8' + '9' * ((n-3)//2) + '8' + '9' * ((n-3)//2) + '8'
        elif k == 8:
            if n <= 6:
                return '8' * n
            else:
                return '8' * 3 + '9' * (n-6) + '8' * 3
        elif k == 7:
            if n == 1:
                return '7'
            elif n == 2:
                return '77'
            elif n % 2 == 0:
                for i in range(9,-1,-1):
                    cur = '9' * (n//2 - 1) + str(i) + str(i) + '9' * (n//2 -1)
                    if int(cur) % 7 == 0:
                        return cur
            else:
                for i in range(9,-1,-1):
                    cur = '9' * (n//2) + str(i) + '9' * (n//2)
                    if int(cur) % 7 == 0:
                        return cur
