class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k==1 or k==3 or k==9){
            std::string res="";
            for(int i=1;i<=n;i++) res+='9';
            return res;
        }
        if(k==2){
            if(n==1) return "8";
            std::string res="";
            res+='8';
            for(int i=1;i<=n-2;i++) res+='9';
            res+='8';
            return res;
        }
        if(k==4){
            if(n<=4) return std::string(n,'8');
            else{
                std::string res="";
                res+="88";
                for(int i=1;i<=n-4;i++) res+='9';
                res+="88";
                return res;
            }
        }
        if(k==5){
            if(n==1) return "5";
            std::string res="";
            res+='5';
            for(int i=1;i<=n-2;i++) res+='9';
            res+='5';
            return res;
        }
        if(k==6){
            if(n<=2) return std::string(n,'6');
            else{
                if(n&1){
                    int cnt=(n-3)/2;
                    std::string res="";
                    res+='8';
                    for(int i=1;i<=cnt;i++) res+='9';
                    res+='8';
                    for(int i=1;i<=cnt;i++) res+='9';
                    res+='8';
                    return res;
                }else{
                    int cnt=(n-4)/2;
                    std::string res="";
                    res+='8';
                    for(int i=1;i<=cnt;i++) res+='9';
                    res+="77";
                    for(int i=1;i<=cnt;i++) res+='9';
                    res+='8';
                    return res;
                }
            }
        }
        if(k==7){
            if(n<=2) return std::string(n,'7');
            std::vector<int> f(n+2,1);
            for(int i=1;i<=n;i++) f[i]=f[i-1]*10,f[i]%=7;
            if(n&1){
                int sum=0;
                for(int i=n-1,j=0;i-j>1;i--,j++){
                    sum+=9*(f[i]+f[j]);
                    sum%=7;
                }
                int b=f[n/2];
                int ans=-1;
                for(int i=9;i>=0;i--) {
                    if(((b*i)%7+sum)%7==0){
                        ans=i;
                        break;
                    }
                }
                std::string res="";
                int cnt=(n-1)/2;
                for(int i=1;i<=cnt;i++) res+='9';
                res+=ans+'0';
                for(int i=1;i<=cnt;i++) res+='9';
                return res;
            }else{
                int sum=0;
                for(int i=n-1,j=0;i-j>1;i--,j++){
                    sum+=9*(f[i]+f[j]);
                    sum%=7;
                }
                int b=f[n/2]+f[n/2-1];
                int ans=-1;
                for(int i=9;i>=0;i--) {
                    if(((b*i)%7+sum)%7==0){
                        ans=i;
                        break;
                    }
                }
                std::string res="";
                int cnt=(n-2)/2;
                for(int i=1;i<=cnt;i++) res+='9';
                res+=ans+'0';
                res+=ans+'0';
                for(int i=1;i<=cnt;i++) res+='9';
                return res;
            }
        }
        if(k==8){
            if(n<=6) return std::string(n,'8');
            std::string res="";
            res+="888";
            for(int i=1;i<=n-6;i++) res+='9';
            res+="888";
            return res;
        }
        return "";
    }
};
