class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        ans = list('9'*n)

        if k == 1 or k == 3 or k == 9: pass

        if k == 2:
            ans[0] = ans[-1] = '8'
        if k == 5:
            ans[0] = ans[-1] = '5'
        if k == 4:
            if n <= 4:
                ans = list('8'*n)
            else:
                ans[0] = ans[1] = ans[-1] = ans[-2] = '8'
        if k == 8:
            if n <= 6:
                ans = list('8'*n)
            else:
                ans[0] = ans[1] = ans[2] = ans[-1] = ans[-2] = ans[-3] = '8'
        if k == 7:
            if n <= 2:
                ans = list('7'*n)
            elif n == 3:
                ans = list('959')
            elif n == 4:
                ans = list('9779')
            elif n == 5:
                ans = list('99799')
            else:
                r = n // 6
                p = n % 6
                if r % 2 == 1:
                    if p == 1 or p == 5:
                        ans[n//2] = '4'
                    elif p == 2 or p == 4:
                        ans[n//2-1] = ans[n//2] = '4'
                    elif p == 3:
                        ans[n//2] = '6'
                else:
                    if p == 1 or p == 5:
                        ans[n//2] = '7'
                    elif p == 2 or p == 4:
                        ans[n//2-1] = ans[n//2] = '7'
                    elif p == 3:
                        ans[n//2] = '5'
        if k == 6:
            if n <= 2:
                ans = list('6'*n)
            else:
                if n % 2 == 1:
                    tot = 16 + 9 * (n-2)
                    mid = 9 - tot % 3
                    ans[0] = ans[-1] = '8'
                    ans[n//2] = chr(ord('0') + mid)
                else:
                    tot = 16 + 9 * (n-2)
                    ans[0] = ans[-1] = '8'
                    if tot % 3 == 1:
                        ans[n//2-1] = ans[n//2] = '7'
                    elif tot % 3 == 2:
                        ans[n//2-1] = ans[n//2] = '8'

        ans = ''.join(ans)
        return ans
