class Solution {
    bool isD7(string &s,int &n){
        int num=0;
        for(int i=0;i<n;i++){
            num=num*10+(s[i]-'0');
            num%=7;
        }
        if(num)return 0;
        return 1;
    }
public:
    string largestPalindrome(int n, int k) {
        string s;
        if((k==1) || (k==3) || (k==9)){
            for(int i=0;i<n;i++){
                s.push_back('9');
            }
            return s;
        }
        if(k==2){
            for(int i=0;i<n;i++){
                s.push_back('9');
            }
            s[0]='8';
            s[n-1]='8';
            return s;
        }
        if(k==4){
            for(int i=0;i<n;i++){
                s.push_back('9');
            }
            s[0]='8';
            s[n-1]='8';
            if(n>1){
                s[1]='8';
                s[n-2]='8';
            }
            return s;
        }
        if(k==5){
            for(int i=0;i<n;i++){
                s.push_back('9');
            }
            s[0]='5';
            s[n-1]='5';
            return s;
        }
        if(k==6){
            if(n==1)return "6";
            if(n==2)return "66";
            int hf=n/2;
            for(int i=0;i<n;i++){
                s.push_back('9');
            }
            s[0]='8',s[n-1]='8';
            if(n&1){
                s[hf]='8';
            }
            else {
                s[hf-1]='7';
                s[hf]='7';
            }
            return s;
        }
        if(k==7){
            int hf=n/2;
            for(int i=0;i<hf;i++){
                s.push_back('9');
            }
            string str="9876543210";
            if(n&1){
                s+=s+'9';
                for(char ch:str){
                    s[hf]=ch;
                    if(isD7(s,n))return s;
                }
            }
            else {
                s+=s;
                for(char ch:str){
                    s[hf-1]=ch;
                    s[hf]=ch;
                    if(isD7(s,n))return s;
                }
            }
        }
        if(k==8){
            for(int i=0;i<n;i++){
                s.push_back('8');
            }
            for(int i=3;i<n-3;i++){
                s[i]='9';
            }
            return s;
        }
        return s;
    }
};
