class Solution {
    public String largestPalindrome(int n, int k) {
        if (n == 1) return switch (k) {
            case 1 -> "9";
            case 2 -> "8";
            case 3 -> "9";
            case 4 -> "8";
            case 5 -> "5";
            case 6 -> "6";
            case 7 -> "7";
            case 8 -> "8";
            default -> "9";
        };
        return switch (k) {
            case 2 -> "8" + "9".repeat(n - 2) + "8";
            case 4 -> k4(n);
            case 5 -> "5" + "9".repeat(n - 2) + "5";
            case 6 -> k6(n);
            case 7 -> k7(n);
            case 8 -> k8(n);
            default -> "9".repeat(n);
        };
    }

    private String k4(int n) {
        if (n < 4) return "8".repeat(n);
        else return "88" + "9".repeat(n - 4) + "88";
    }

    private String k6(int n) {
        if (n == 2) return "66";
        if (n == 3) return "888";
        if (n % 2 == 1) {
            return "8" + "9".repeat(n / 2 - 1) + "8" + "9".repeat(n / 2 - 1) + "8";
        } else {
            return "8" + "9".repeat(n / 2 - 2) + "77" + "9".repeat(n / 2 - 2) + "8";
        }
    }

    private String k7(int n) {
        if (n == 2) return "77";
        if (n == 3) return "959";
        if (n % 6 == 0) return "9".repeat(n);
        int tmp = n % 6;
        if ((n / 6) % 2 == 0) {
            if (tmp == 1 || tmp == 5) return "9".repeat((n - 1) / 2) + "7" + "9".repeat((n - 1) / 2);
            else if (tmp == 2 || tmp == 4) return "9".repeat((n - 2) / 2) + "77" + "9".repeat((n - 2) / 2);
            else return "9".repeat((n - 1) / 2) + "5" + "9".repeat((n - 1) / 2);
        } else {
            if (tmp == 1 || tmp == 5) return "9".repeat((n - 1) / 2) + "4" + "9".repeat((n - 1) / 2);
            else if (tmp == 2 || tmp == 4) return "9".repeat((n - 2) / 2) + "44" + "9".repeat((n - 2) / 2);
            else return "9".repeat((n - 1) / 2) + "6" + "9".repeat((n - 1) / 2);
        }
    }

    private String k8(int n) {
        if (n < 7) return "8".repeat(n);
        return "888" + "9".repeat(n - 6) + "888";
    }
}
