def check(a, n):
    mod = 0
    for i in range(n):
        mod *= 10
        mod += int(a[i])
        mod %= 7
    if mod==0: return 1
    return 0
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k in [1,3,9]:
            return "9"*n
        if k==5:
            if n<=2: return "5"*n
            return "5"+"9"*(n-2)+"5"
        if k==2:
            if n<=2: return "8"*n
            return "8"+"9"*(n-2)+"8"
        if k==4:
            if n<=4: return "8"*n
            return "88"+"9"*(n-4)+"88"
        if k==8:
            if n<=6: return "8"*n
            return "888"+"9"*(n-6)+"888"
        if k==6:
            if n%2:
                if n==1: return "6"
                if n==3: return"888"
                a = []
                for i in range(n):
                    if i in [0, n-1, n//2]:
                        a.append("8")
                    else:
                        a.append("9")
                return "".join(a)
            if n==2: return "66"
            if n==4: return "8778"
            x = (n-6)//2
            return "8"+"9"*x+"9779"+"9"*x+"8"
        if n<=2: return "7"*n
        x = (n-1)//2
        for i in range(9, -1,-1):
            s = []
            for j in range(x):
                s.append("9")
            for j in range(n-x*2):
                s.append(str(i))
            for i in range(x):
                s.append("9")
            if check(s, n):
                return "".join(s)
