class Solution {
    fun largestPalindrome(n: Int, k: Int): String {
        when (k) {
            1, 3, 9 -> {
                val s = StringBuilder()
                repeat(n) { s.append('9') }
                return s.toString()
            }

            2 -> {
                if (n == 1) return "8"
                val s = StringBuilder()
                s.append('8')
                repeat(n - 2) { s.append('9') }
                s.append('8')
                return s.toString()
            }
            4 -> {
                if (n == 1) return "8"
                if (n == 2) return "88"
                if (n == 3) return "888"
                val s = StringBuilder()
                s.append("88")
                repeat(n - 4) { s.append('9') }
                s.append("88")
                return s.toString()
            }
            5 -> {
                if (n == 1) return "5"
                val s = StringBuilder()
                s.append("5")
                repeat(n - 2) { s.append('9') }
                s.append("5")
                return s.toString()
            }
            8 -> {
                if (n == 1) return "8"
                if (n == 2) return "88"
                if (n == 3) return "888"
                if (n == 4) return "8888"
                if (n == 5) return "88888"
                val s = StringBuilder()
                s.append("888")
                repeat(n - 6) { s.append('9') }
                s.append("888")
                return s.toString()
            }
            6 -> {
                if (n == 1) return "6"
                if (n == 2) return "66"
                if (n == 3) return "888"
                val s = StringBuilder()
                s.append("8")
                if (n % 2 == 0) {
                    repeat(n / 2 - 2) {s.append("9")}
                    s.append("77")
                    repeat(n / 2 - 2) {s.append("9")}
                } else {
                    repeat(n / 2 - 1) {s.append("9")}
                    s.append("8")
                    repeat(n / 2 - 1) {s.append("9")}

                }
                s.append("8")
                return s.toString()
            }
            7 -> {
                if (n == 1) return "7"
                if (n == 2) return "77"
                for (i in 9 downTo 0) {
                    val s = StringBuilder()
                    repeat((n + 1) / 2 - 1) { s.append("9") }
                    if (n % 2 == 0) { s.append("$i$i")} else s.append("$i")
                    repeat((n + 1) / 2 - 1) { s.append("9") }    
                    if (test7(s.toString())) return s.toString()
                }
            }
        }
        print(test7("999969999"))
        return ""
    }


    fun test7(str: String): Boolean {
        var c = 0
        for (i in str.indices) {
            val s = str[i] - '0'
            c *= 10
            c += s
            c %= 7
        }
        return c == 0
    }
}
