class Solution {
public:
    string largestPalindrome(int n, int k) {

        if(k==1 || k == 3 || k == 9){
            string ans = "";
            for(int i=0;i<n;i++){
                ans+="9";
            }
            return ans;
        }
        else if(k == 2){
            string ans="8";
            for(int i = 1;i<n-1;i++){
                ans+="9";
            }
            if(n>1)
                ans+="8";
            return ans;
        }else if(k==4){
            string ans="8";
            if(n > 1){
                ans+="8";
            }
            for(int i = 2;i<n-2;i++){
                ans+="9";
            }
            if(n>2)
                ans+="8";
            if(n>3){
                ans+="8";
            }
            return ans;
        }else if(k==5){
            string ans="5";
            for(int i = 1;i<n-1;i++){
                ans+="9";
            }
            if(n>1)
                ans+="5";
            return ans;
        }else if(k==6){
            string ans="";
            if(n==1){
                ans+="6";
                return ans;
            }else if(n==2){
                return "66";
            }
            ans+="8";
            string temp="";
            for(int i=1;i< (n-1)/2;i++){
                ans+="9";
                temp+="9";
            }

            if(n%2==0){
                ans+="77";
            }else{
                ans+="8";
            }
            ans+= temp;
            ans+="8";
            return ans;
        }else if(k==7){
            if(n==1)return "7";
            else if(n==2)return "77";
            if(n==3)return "959";

            string ans="";
            string temp="";
            for(int i=0;i<(n-1)/2;i++){
                ans+="9";
                temp+="9";
            }

            for(char i='9';i>='0';i--){
                string check = "";
                check+= i;

                if(n%2==0){
                    check+=i;
                }

                string is = ans + check + temp;
                int remainder =0;
                for(char digit: is){
                    remainder = (remainder * 10 + (digit - '0')) % 7;
                }
                if(remainder == 0)return is;
            }


        }else if(k==8){
            if(n ==1)return "8";
            else if(n==2)return "88";
            else if(n==3)return "888";
            else if(n==4)return "8888";
            else if(n==5)return "88888";

            string ans = "888";
            for(int i=3;i< n-3;i++){
                ans+="9";
            }
            ans+="888";
            return ans;
        }
        return "";
    }
};
