class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans;
        for (int i = 0; i < n; i++) ans += '9';
        switch (k) {
            case 1:
                return ans;
            case 2:
                ans[0] = '8';
                ans[n-1] = '8';
                return ans;
            case 3:
                return ans;
            case 4:
                ans[0] = '8';
                ans[n-1] = '8';
                if (n > 2) {
                    ans[1] = '8';
                    ans[n-2] = '8';
                }
                return ans;
            case 5:
                ans[0] = '5';
                ans[n-1] = '5';
                return ans;
            case 6:
                if (n == 1) ans = "6";
                else if (n == 2) ans = "66";
                else {
                    ans[0] = '8';
                    ans[n-1] = '8';
                    int tmp = 16;
                    if (n % 2 == 1) {
                        tmp += (n-3) * 9;
                        for (int i = 9; i >= 0; i--) {
                            if ((tmp + i) % 6 == 0) {
                                ans[n >> 1] = '0' + i;
                                break;
                            }
                        }
                    }
                    else {
                        tmp += (n-4) * 9;
                        for (int i = 9; i >= 0; i--) {
                            if ((tmp + i*2) % 6 == 0) {
                                ans[(n >> 1) - 1] = '0' + i;
                                ans[n >> 1] = '0' + i;
                                break;
                            }
                        }
                    }
                }
                return ans;
            case 7:
                if (n == 1) ans = "7";
                else if (n == 2) ans = "77";
                else {
                    int m[7] = {3, 2, 6, 4, 5, 1};
                    int cur_mod = m[(n - 1) % 6] - 1;
                    if (n % 2 == 1) {
                        int minus_mod = m[((n >> 1) - 1) % 6];
                        for (int i = 9; i >= 0; i--) {
                            if (cur_mod % 7 == 0) {
                                ans[n >> 1] = '0' + i;
                                break;
                            }
                            cur_mod -= minus_mod;
                        }
                    }
                    else {
                        int minus_mod = m[((n >> 1) - 2) % 6];
                        for (int i = 9; i >= 0; i--) {
                            if (cur_mod % 7 == 0) {
                                ans[n >> 1] = '0' + i;
                                ans[(n >> 1) - 1] = '0' + i;
                                break;
                            }
                            cur_mod -= minus_mod * 11;
                        }
                    }
                }
                return ans;
            case 8:
                ans[0] = '8';
                ans[n-1] = '8';
                if (n > 2) {
                    ans[1] = '8';
                    ans[n-2] = '8';
                }
                if (n > 4) {
                    ans[2] = '8';
                    ans[n-3] = '8';
                }
                return ans;
            case 9:
                return ans;
            default:
                return ans;
        }
    }
};
