class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return '9' * n
        if k == 2:
            if n == 1: return '8'
            if n == 2: return '88'
            return '8' + (n - 2) * '9' + '8'
        if k == 4:
            if n < 8:
                return str([0, 8, 88, 888, 8888, 88988, 889988, 8899988, 88999988][n])
            return '8' * 2 + '9' *(n - 4) +  '8' * 2

        if k == 5:
            if n == 1: return '5'
            if n == 2: return '55'
            return '5' + (n - 2) * '9' + '5'
        if k == 6:
            if n == 1: return '6'
            if n == 2: return '66'
            if n == 3: return '888'
            if n & 1:
                return '8' + '9' * ((n - 3)  // 2) + '8' + '9' * ((n - 3)  // 2) + '8'
            return '8'  + '9' * ((n - 4) // 2) + '77' + '9' * ((n - 4) // 2) + '8'
        if k == 7:
            if n < 15:
                return str([0,7, 77, 959, 9779, 99799, 999999, 9994999, 99944999, 999969999, 9999449999, 99999499999, 999999999999, 9999997999999, 99999977999999][n])
            if n & 1:
                tail = '9' * ((n - 1) // 2)
                head = tail + '0' * (n - len(tail))
                remain = int(head) % 7 + int(tail) % 7
                for i in range(9, 0, -1):
                    if (i * 10 ** len(tail) + remain) % 7 == 0:
                        return tail + str(i) + tail
            else:
                tail = '9' * ((n - 1) // 2)
                head = tail + '0' * (n - len(tail))
                remain = int(head) % 7 + int(tail) % 7
                for i in range(9, 0, -1):
                    if ((i * 10 + i) * 10 ** len(tail) + remain) % 7 == 0:
                        return tail + str(i)* 2 + tail
        if k == 8:
            if n < 8:
                return str([0, 8, 88, 888, 8888, 88888, 888888, 8889888][n])
            return '8' * 3 + '9' *(n - 6) +  '8' * 3
