impl Solution {

    pub fn largest_palindrome(n: i32, k: i32) -> String {
        let (n, k) = (n as usize, k as usize);
        if k == 1 || k == 3 || k == 9 {
            return "9".repeat(n);
        }
        if n == 1 {
            return [0, 9, 8, 9, 8, 5, 6, 7, 8, 9][k].to_string();
        }
        if k == 2 {
            return "8".to_string() + &"9".repeat(n-2) + "8";
        }
        if k == 4 {
            if n <= 3 {
                return "8".repeat(n);
            }
            return "88".to_string() + &"9".repeat(n-4) + "88";
        }
        if k == 5 {
            return "5".to_string() + &"9".repeat(n-2) + "5";
        }
        if k == 6 {
            if n == 2 {
                return "66".into();
            }
            let r = n - 2;
            if n & 1 == 0 {
                return "8".to_string() + &"9".repeat((n-4)/2) + "77" + &"9".repeat((n-4)/2) + "8";
            }
            return "8".to_string() + &"9".repeat((n-3)/2) + "8" + &"9".repeat((n-3)/2) + "8";
        }
        if k == 7 {
            if n == 2 {
                return "77".into();
            }
            if n == 3 {
                return "959".into();
            }
            let p = (n-1)/2;
            let mut x = 0;
            let mut y = 1;
            for i in 0..p {
                x = (x * 10 + 9) % 7;
                y = y * 10 % 7;
            }
            for i in (0..10).rev() {
                let a = if n & 1 == 1 {
                    (x * 10 + i) * y + x
                } else {
                    (x * 100 + 11*i)*y+x
                };
                if a % 7 == 0 {
                    return "9".repeat(p) + &i.to_string().repeat(n-2*p) + &"9".repeat(p);
                }
            }


        }
        if k == 8 {
            if n <= 6 {
                return "8".repeat(n);
            }
            return "8".repeat(3) + &"9".repeat(n-6) + &"8".repeat(3);
        }
        "".into()
    }

}
