class Solution {
public:
    // Function to check if a number represented as a string is divisible by 7
    bool isDivisibleBy7(const std::string &numStr) {
        int n = numStr.size();
        int num = 0;

        // Loop through each character of the string
        for (char ch : numStr) {
            // Convert the character to an integer digit and update the number
            num = num * 10 + (ch - '0');
            // Apply the divisibility by 7 rule
            num %= 7;

        }

        return (num == 0);
    }

    // Function to generate the largest palindrome of length n that is divisible by k
    string largestPalindrome(int n, int k) {
        string s(n, '9');

        if (k == 1 || k == 3 || k == 9) {
            return s;
        }
        if (k == 2) {
            s[0] = s[n - 1] = '8';
            return s;
        }
        if (k == 4) {
            if (n == 1)
                return "8";
            s[0] = s[1] = s[n - 1] = s[n - 2] = '8';
            return s;
        }
        if (k == 5) {
            s[0] = s[n - 1] = '5';
            return s;
        }
        if (k == 8) {
            if (n == 1)
                return "8";
            if (n == 2) 
                return "88";
            s[0] = s[1] = s[n - 1] = s[n - 2] = s[n - 3] = s[2] = '8';
            return s;
        }
        if (k == 6) {
            if (n == 1) 
                return "6";
            if (n == 2) 
                return "66";
            s[0] = s[n - 1] = '8';
            if (n % 2 != 0) {
                s[n / 2] = '8';
                return s;
            } else {
                s[n / 2] = s[n / 2 - 1] = '7';
                return s;
            }
        }
        if (k == 7) {
            int m1 = n / 2, m2 = n / 2;
            if (n % 2 == 0)
                m2 = n / 2 - 1;
            for (char i = '9'; i >= '0'; i--) {
                s[m1] = s[m2] = i;
                if (isDivisibleBy7(s)) {
                    return s;
                }
            }
        }

        return s;
    }
};
