class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        res = ''
        if k == 1 or k == 3 or k == 9:
            res += '9' * n
        elif k == 2:
            if n > 2:
                res = '8' + '9' * (n-2) + '8'
            else:
                res = '8' * n
        elif k == 4:
            if n > 4:
                res = '88' + '9' * (n-4) + '88'
            else:
                res = '8' * n
        elif k == 8:
            if n > 6:
                res = '888' + '9' * (n-6) + '888'
            else:
                res = '8' * n
        elif k == 5:
            if n > 2:
                res = '5' + '9' * (n-2) + '5'
            else:
                res = '5' * n
        elif k == 6:
            if n <= 2:
                res = '6' * n
            elif n % 2 == 0:
                if n == 4:
                    res = '8778'
                else:
                    t = (n - 4) // 2
                    res = '8' + '9' * t + '77' + '9' * t + '8'
            else:
                if n == 3:
                    res = '888'
                else:
                    t = (n - 3) // 2
                    res = '8' + '9' * t + '8' + '9' * t + '8'
        else:
            if n % 2 == 0:
                t = (n - 2) // 2
                for i in range(9, -1, -1):
                    temp = '9' * t + str(i) * 2 + '9' * t
                    x = 0
                    for a in temp:
                        x = 10 * x + int(a)
                        x %= 7
                    if x == 0:
                        res = temp
                        break
            else:
                t = (n - 1) // 2
                for i in range(9, -1, -1):
                    temp = '9' * t + str(i) + '9' * t
                    x = 0
                    for a in temp:
                        x = 10 * x + int(a)
                        x %= 7
                    if x == 0:
                        res = temp
                        break
        return res
