class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        #  6 7 
        if k==1 or k==3 or k==9:
            return "9"*n
        elif k==2:
            if n==1:
                return "8"
            elif n==2:
                return "88"
            else:
                return "8"+"9"*(n-2)+"8"

        elif k==4:
            if n==1:
                return "8"
            elif n==2:
                return "88"
            elif n==3:
                return "888"
            else:
                return "88"+"9"*(n-4)+"88"
        elif k==5:
            if n==1:
                return "5"
            else:
                return "5"+"9"*(n-2)+"5"
        elif k==6:
            if n==1:
                return '6'
            elif n==2:
                return '66'
            elif n==3: # 4:8778 5:89898 6:899778
                return '888'
            else:
                if n%2==0:
                    return '8'+(n//2-2)*'9'+'77'+(n//2-2)*'9'+'8'
                else:
                    return '8'+(n//2-1)*'9'+'8'+(n//2-1)*'9'+'8'
        elif k==7:
            if n==1:
                return '7'
            elif n==2:
                return '77'
            else:
                lo=[
                    '','7','77','959',
                    '9779','99799','999999','9994999',
                    '99944999','999969999','9999449999','99999499999']
                s=n//12*6
                return s*'9'+lo[n%12]+s*'9'
        elif k==8:
            if n<6:
                return "8"*n
            else:
                return "888"+"9"*(n-6)+"888"
        else:
            return "0"
