class Solution {
    bool divisible(string &ans)
    {
        int rem = 0;
        for (auto &dig : ans)
        {
            rem = (rem * 10 + (dig - '0')) % 7;
        }
        return rem == 0;
    }
public:
    string largestPalindrome(int n, int k) {
        string ans;
        ans.resize(n);

        if(k == 1 || k == 3 || k == 9){
            ans.assign(n, '9');
        }
        else if(k == 2){
            ans[0] = ans[n-1] = '8';
            for(int i = 1; i < n-1; ++i) ans[i] = '9';
        }
        else if(k == 4){
            ans[0] = ans[n-1] = '8';
            if(n > 2) ans[1] = ans[n-2] = '8';
            for(int i = 2; i < n-2; ++i) ans[i] = '9';
        }
        else if(k == 5){
            ans[0] = ans[n-1] = '5';
            for(int i = 1; i < n-1; ++i) ans[i] = '9';
        }
        else if(k == 6){
            if(n == 1) ans = "6";
            else if(n == 2) ans = "66";
            else if(n == 3) ans = "888";
            else{
                ans[0] = ans[n-1] = '8';
                for(int i = 1; i < n-1; ++i) ans[i] = '9';

                if(n & 1){
                    ans[n/2] = '8';
                }
                else {
                    ans[n/2] = '7';
                    ans[(n/2)-1] = '7';
                }

            }
        }
        else if(k == 7){
            if(n == 1) ans = "7";
            else if(n == 2) ans = "77";
            else{
                ans.assign(n, '9');
                if(n&1){
                    for(char dig = '9'; dig >= '0'; --dig){
                        ans[n/2] = dig;
                        if(divisible(ans)){
                            break;
                        }
                    }
                }
                else{
                    for(char dig = '9'; dig >= '0'; --dig){
                        ans[n/2] = dig;
                        ans[(n/2)-1] = dig;
                        if(divisible(ans)){
                            break;
                        }
                    }
                }
            }
        }
        else if(k == 8){
            ans.assign(n, '8');
            for(int i = 3; i < n-3; ++i)
                ans[i] = '9';
        }

        return ans;
    }
};
