int checkMod7(string s){
    int mod = 0;
    int len = s.size();
    int power = 1;
    for(int i = len - 1; i >= 0; i--) {
        mod += ((s[i] - '0') * power) % 7;
        power = (power * 10) % 7;
        mod %= 7;
    }
    return mod == 0;
}

class Solution {
public:
    string largestPalindrome(int len, int d) {

        if(len == 1) {
            char ch;
            if(d == 1 || d == 3 || d == 9) ch = '9';
            else if(d == 2 || d == 4 || d == 8) ch = '8';
            else if(d == 5) ch = '5';
            else if(d == 6) ch = '6';
            else ch = '7';
            return string(1, ch);
        }

        if(d == 1 || d == 3 || d == 9) return string(len, '9');
        if(d == 2) return "8" + string(len - 2, '9') + "8";
        if(d == 5) return "5" + string(len - 2, '9') + "5";
        if(d == 4) {
            string res(len, '9');
            res[0] = res[len - 1] = '8';
            if(len > 2) res[1] = res[len - 2] = '8';
            return res;
        }
        if(d == 8) {
            string res(len, '9');
            res[0] = res[len - 1] = '8';
            if(len>=3) res[1]='8';
            if(len>=4) res[len-2]='8';
            if(len>=5) res[len-3]='8';
            if(len>=6) res[2]='8';
            return res;
        }
        if(d == 6) {
            if(len == 2) return string(2, '6');
            string res(len, '9');
            res[0] = res[len - 1] = '8';
            if(len % 2 == 0) {
                res[len / 2 - 1] = res[len / 2] = '7';
            } else {
                res[len / 2] = '8';
            }
            return res;
        }
        if(d == 7) {
            string res(len, '9');
            if(len % 2 == 1) {
                for(int i = 9; i >= 0; i--) {
                    res[len / 2] = '0' + i;
                    if(checkMod7(res)) return res;
                }
            } else {
                for(int i = 9; i >= 0; i--) {
                    res[len / 2 - 1] = res[len / 2] = '0' + i;
                    if(checkMod7(res)) return res;
                }
            }
        }
        return "";
    }
};
