class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (n<=6) {
            for (int i=9;i>=0;i--) {
                if (n==1&&i%k==0) {
                    string s="";
                    s+=('0'+i);
                    return s;
                }
                if (n==2&&(i*11)%k==0) {
                    string s="";
                    s+=('0'+i);
                    s+=('0'+i);
                    return s;
                }
                for (int j=9;j>=0;j--) {
                    if (n==3&&(i*101+j*10)%k==0) {
                        string s="";
                        s+=('0'+i);
                        s+=('0'+j);
                        s+=('0'+i);
                        return s;
                    }
                    if (n==4&&(i*1001+j*110)%k==0) {
                        string s="";
                        s+=('0'+i);
                        s+=('0'+j);
                        s+=('0'+j);
                        s+=('0'+i);
                        return s;
                    }
                    for (int r=9;r>=0;r--) {
                        if (n==5&&(i*10001+j*1010+r*100)%k==0) {
                            string s="";
                            s+=('0'+i);
                            s+=('0'+j);
                            s+=('0'+r);
                            s+=('0'+j);
                            s+=('0'+i);
                            return s;
                        }
                        if (n==6&&(i*100001+j*10010+r*1100)%k==0) {
                            string s="";
                            s+=('0'+i);
                            s+=('0'+j);
                            s+=('0'+r);
                            s+=('0'+r);
                            s+=('0'+j);
                            s+=('0'+i);
                            return s;
                        }   
                    }
                }
            }
        }
        int h=(n-1)/2;
        if (k==1) {
            string s="";
            for (int i=0;i<n;i++)
                s+='9';
            return s;
        }
        if (k==2) {
            string s="8";
            for (int i=0;i<n-2;i++)
                s+='9';
            s+='8';
            return s;
        }
        if (k==3) {
            string s="";
            for (int i=0;i<n;i++)
                s+='9';
            return s;
        }
        if (k==4) {
            string s="88";
            for (int i=0;i<n-4;i++)
                s+='9';
            s+="88";
            return s;
        }
        if (k==5) {
            string s="5";
            for (int i=0;i<n-2;i++)
                s+='9';
            s+='5';
            return s;
        }
        if (k==6) {
            if (n%2==1) {
                string s="8";
                for (int i=0;i<h-1;i++)
                    s+='9';
                s+='8';
                for (int i=0;i<h-1;i++)
                    s+='9';
                s+='8';
                return s;
            } else {
                string s="8";
                for (int i=0;i<h-1;i++)
                    s+='9';
                s+="77";
                for (int i=0;i<h-1;i++)
                    s+='9';
                s+='8';
                return s;
            }
        }
        if (k==8) {
            string s="888";
            for (int i=0;i<n-6;i++)
                s+='9';
            s+="888";
            return s;
        }
        if (k==9) {
            string s="";
            for (int i=0;i<n;i++)
                s+='9';
            return s;
        }

        for (int r=9;r>=0;r--) {
            for (int i=9;i>=0;i--) {
                int yu=r%k;
                for (int j=1;j<h;j++) {
                    yu=yu*10+9;
                    yu=yu%k;
                }
                for (int j=0;j<=(n+1)%2;j++) {
                    yu=yu*10+i;
                    yu=yu%k;
                }
                for (int j=1;j<h;j++) {
                    yu=yu*10+9;
                    yu=yu%k;
                }
                yu=yu*10+r;
                yu=yu%k;
                if (yu==0) {
                    string s="";
                    s+=('0'+r);
                    for (int j=1;j<h;j++)
                        s+='9';
                    for (int j=0;j<=(n+1)%2;j++)
                        s+=('0'+i);
                    for (int j=1;j<h;j++)
                        s+='9';
                    s+=('0'+r);
                    return s;
                }
            }
        }
        return "0";
    }
};
