import java.math.*;
class Solution {
    public String largestPalindrome(int n, int k) {
        StringBuilder sb = new StringBuilder();
        if(k == 1 || k == 3 || k == 9) {
            for(int i = 0; i < n; i++) {
                sb.append(9);
            }
        }
        else if(k == 5) {
            for(int i = 0; i < n; i++) {
                if(i == 0 || i == n - 1) sb.append(5);
                else sb.append(9);
            }
        }
        else if(k == 2) {
            for(int i = 0; i < n; i++) {
                if(i == 0 || i == n - 1) sb.append(8);
                else sb.append(9);
            }
        }
        else if(k == 4) {
            for(int i = 0; i < n; i++) {
                if(i < 2 || i >= n - 2) sb.append(8);
                else sb.append(9);
            }
        }
        else if(k == 8) {
            for(int i = 0; i < n; i++) {
                if(i < 3 || i >= n - 3) sb.append(8);
                else sb.append(9);
            }
        }
        else if(k == 6) {
            for(int i = 0; i < n; i++) {
                if(n < 3) sb.append(6);
                else if(i == 0 || i == n - 1) sb.append(8);
                else if(n % 2 == 0 && (i == n / 2 || i == n / 2 - 1)) sb.append(7);
                else if(n % 2 == 1 && (i == n / 2)) sb.append(8);
                else sb.append(9);
            }
        }
        else if(k == 7) {
            for(int i = 0; i < n; i++) {
                if(n < 3) sb.append(7);
                else if(n % 2 == 0 && (i == n / 2 || i == n / 2 - 1)) sb.append('.');
                else if(n % 2 == 1 && (i == n / 2)) sb.append('.');
                else sb.append(9);
            }
            for(int j = 9; j >= 0; j--) {
                BigInteger val = new BigInteger(sb.toString().replace(".", "" + j));
                if(val.mod(BigInteger.valueOf(7)) == BigInteger.ZERO) return val.toString();
            }
        }
        return sb.toString();
    }
}
