class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(n == 1) {
            if(k == 1 || k == 3|| k == 9) return "9";
            if(k == 2 || k == 4) return "8";
            string ans = "";
            char x = k+'0';
            ans += x;
            return ans;
        }
        if(k == 1 || k == 3 || k == 9){
            string ans = "";
            for(int i = 1;i <= n;i++) ans += "9";
            return ans;
        }
        if(k == 2){
            int mo = n-2;
            string ans = "";
            for(int i = 1;i <= mo;i++) ans += "9";
            ans += "8";
            ans = "8"+ans;
            return ans;
        }
        if(k == 4){
            if(n == 2) return "88";
            if(n == 3) return "888";
            int mo = n-4;
            string ans = "";
            for(int i = 1;i <= mo;i++) ans += "9";
            ans += "88";
            ans = "88"+ans;
            return ans;
        }
        if(k == 5){
            int mo = n-2;
            string ans = "";
            for(int i = 1;i <= mo;i++) ans += "9";
            ans += "5";
            ans = "5"+ans;
            return ans;
        }
        if(k == 6){
            if(n == 1) return "6";
            if(n == 2) return "66";
            if(n == 3) return "888";
            char ss[100005];
            for(int i = 1;i <= n;i++) ss[i] = '-';
            ss[1] = ss[n] = '8';
            if(n&1){
                ss[n/2+1] = '8';
            }else{
                ss[n/2] = '7';
                ss[n/2+1] = '7';
            }
            for(int i = 1;i <= n;i++){
                if(ss[i] == '-') ss[i] = '9';
            }
            string ans = "";
            for(int i = 1;i <= n;i++) ans += ss[i];
            return ans;
        }
        if(k == 8){
            int mo = n-6;
            string ans = "";
            if(n <= 6) {
                for(int i = 1;i <= n;i++) ans += "8";
                return ans;
            }
            for(int i = 1;i <= mo;i++) ans += "9";
            ans += "888";
            ans = "888"+ans;
            return ans;
        }
        if(k == 7){
            if(n <= 2) return "77";
            char ss[100005];
            for(int i = 1;i <= n;i++) ss[i] = '-';
            if(n&1){
                for(int i = 1;i <= n;i++){
                    if(i == n/2+1) continue;
                    else ss[i] = '9';
                }
                for(int j = 9;j >= 0;j--){
                    ss[n/2+1] = j+'0';
                    int res = 0;
                    for(int i = 1;i <= n;i++){
                       res = res*10+(ss[i]-'0');
                       res %= 7;
                    }
                    if(res == 0){
                        string ans = "";
                        for(int i = 1;i <= n;i++) ans += ss[i];
                        return ans;
                    }
                }
            }
            else{
                for(int i = 1;i <= n;i++){
                    if(i == n/2+1 || i == n/2) continue;
                    else ss[i] = '9';
                }
                for(int j = 99;j >= 0;j-=11){
                    ss[n/2] = j/10+'0';
                    ss[n/2+1] = j%10+'0';

                    int res = 0;
                    for(int i = 1;i <= n;i++){
                       res = res*10+(ss[i]-'0');
                       res %= 7;
                    }
                    if(res == 0){
                        string ans = "";
                        for(int i = 1;i <= n;i++) ans += ss[i];
                        return ans;
                    }
                }
            }
        }
        return "";
    }
};
