class Solution {

string solve(int n) {
    vector<int> powers(n);
    powers[0] = 1;
    for (int i = 1; i < n; ++i) {
        powers[i] = (powers[i - 1] * 10) % 7;
    }

    vector<int> digits(n, 9);

    while (true) {
        int num = 0;
        for (int i = 0; i < n; ++i) {
            num = (num + digits[i] * powers[i]) % 7;
        }
        if (num == 0) {
            break;
        }

        int i = n/2;
        while (i >= 0 && digits[i] == 0) {
            digits[i] = 9;
            digits[n-i-1] = 9;
            --i;
        }
     --digits[i];
     if(i != n-i-1) --digits[n-i-1];
    }

    string ans = "";
    for (int i = 0; i < n; ++i) {
        ans += '0' + digits[i];
    }
 return ans;
}
public:
    string largestPalindrome(int n, int k) {
        string ans = "";

        if(k == 1 || k == 3) {
            for(int i=0; i<n; i++) ans += "9";
            return ans;
        }
        else if(k == 2) {
            for(int i=0; i<n; i++) ans += "9";
            ans[0] = '8'; ans.back() = '8';
            return ans;
        }
        else if(k == 4) {
            if(n == 1) return "8";
            for(int i=0; i<n; i++) ans += "9";
            ans[0] = '8'; ans.back() = '8';
            if(n > 2) {
                ans[1] = '8'; ans[n-2] = '8';
            }
            return ans;
        }
        else if(k == 5) {
            for(int i=0; i<n; i++) ans += "9";
            ans[0] = '5'; ans.back() = '5';
            return ans;
        }
        else if(k == 6) {
            if(n == 1) return "6";
            if(n == 2) return "66";
            for(int i=0; i<n; i++) ans += "9";
            ans[0] = '8'; ans.back() = '8';
            int rem = ((n-2)*9 + 16) % 3;
            if(n % 2 == 1) {
                char digit = '0' + 9 - rem;
                ans[n/2] = digit;
                return ans;
            }
            else {
                if(rem == 0) return ans;
                else if(rem == 1) {
                    ans[n/2] = '7'; ans[n/2-1] = '7';
                    return ans;
                }
                else {
                    ans[n/2] = '8'; ans[n/2-1] = '8';
                    return ans;
                }
            }
        }
        else if(k == 7) {
            if(n == 1) return "7";
            if(n == 2) return "77";
            ans = solve(n);
            return ans;
        }
        else if(k == 8) {
            if(n == 1) return "8";
            if(n == 2) return "88";
            for(int i=0; i<n; i++) ans += "9";
            ans[0] = '8'; ans[1] = '8'; ans[2] = '8';
            ans[n-1] = '8'; ans[n-2] = '8'; ans[n-3] = '8';
            return ans;
        }
        else if(k == 9) {
            for(int i=0; i<n; i++) ans += "9";
            return ans;
        }
     return "";
    }
};
