class Solution {
public:
    bool check(char *t, int n) {
        int num = 7;
        int r = 0;
        for(int i=0; i<n; i++) {
            r = ((r*10) + (t[i] - '0'));
            r = (r % 7);
        }
        if(r==0) return true;
        return false;
    }
    string largestPalindrome(int n, int k) {
        //string s = "";
        char t[n];
        if(k==1 || k==3 || k==9) {
            for(int i=0; i<n; i++) {
                t[i] = '9';
            }
        }

        if(k==2) {
            int l=0,r=n-1;
            t[l] = '8';
            t[r] = '8';
            l++;
            r--;
            while(l<=r) {
                t[l] = '9';
                t[r] = '9';
                l++;r--;
            }
        }
        if(k==4) {
            if(n==1) {
                return "8";
            }
            for(int i=0; i<2; i++) {
                t[i] = '8';
            }
            for(int i=2; i<n-2; i++) {
                t[i] = '9';
            }
            for(int i=n-2; i<n; i++) {
                t[i] = '8';
            }
        }
        if(k==5) {
            t[0] = '5';
            t[n-1] = '5';
            for(int i=1; i<n-1; i++) {
                t[i] = '9';
            }
        }
        if(k==6) {
            if(n==1) {
                return "6";
            }
            if(n==2) {
                return "66";
            }
            t[0] = '8';
            t[n-1] = '8';
            if((n&1)==0) {
                int l = ((n/2)-1);
                int r = (n/2);
                t[l] = '7';
                t[r] = '7';
                for(int i=1; i<l; i++) {
                    t[i] = '9';
                    t[r+i] = '9';
                }
            }
            else {
                int l = (n/2);
                t[l] = '8';
                for(int i=1; i<l; i++) {
                    t[i] = '9';
                    t[l+i] = '9';
                }
            }
        }

        if(k==7) {
            if(n&1) {
                int l = (n/2);
                for(int i=0; i<l; i++) {
                    t[i] = '9';
                    t[l+1+i] = '9';
                }
                for(int i=9; i>=0; i--) {
                    t[l] = ('0' + i);
                    bool flag = check(t,n);
                    if(flag) break;
                }
            }
            else {
                int l = ((n/2)-1);
                int r = (n/2);
                for(int i=0; i<l; i++) {
                    t[i] = '9';
                    t[r+1+i] = '9';
                }
                for(int i=9; i>=0; i--) {
                    t[l] = ('0' + i);
                    t[r] = ('0' + i);
                    bool flag = check(t,n);
                    if(flag) break;
                }
            }
        }
        if(k==8) {
            if(n==1) {
                return "8";
            }
            if(n==2) {
                return "88";
            }

            t[0] = '8';
            t[1] = '8';
            t[2] = '8';
            for(int i=3; i<n-3; i++) {
                t[i] = '9';
            }
            for(int i=n-3; i<n; i++) {
                t[i] = '8';
            }
        }
        string s = "";
        for(int i=0; i<n; i++) {
            s.push_back(t[i]);
        }
        cout << s << endl;
        return s;
    }
};
