class Solution {
public:
    bool check7(string num){
        int n = num.length(), gSum=0;
    if (n == 0)
        return 1;

    if (n % 3 == 1) {
        num="00" + num;
        n += 2;
    }
    else if (n % 3 == 2) {
        num= "0" + num;
        n++;
    }
    int i, GSum = 0, p = 1;
    for (i = n - 1; i >= 0; i--) {
        int group = 0;
        group += num[i--] - '0';
        group += (num[i--] - '0') * 10;
        group += (num[i] - '0') * 100;

        gSum = gSum + group * p;
        p *= (-1);
    }

    return (gSum % 7 == 0);
    }
    string largestPalindrome(int n, int k) {
        string s="";
        if(k==1 || k==3 || k==9){
            for(int i=0;i<n;i++){
                s.push_back('9');
            }
            return s;
        }
        if(k==4){
            for(int i=0;i<n;i++){
                s.push_back('8');
            }
            for(int i=2;i<n-2;i++){
                s[i]='9';
            }
            return s;
        }
        if(k==8){
            for(int i=0;i<n;i++){
                s.push_back('8');
            }
            for(int i=3;i<n-3;i++){
                s[i]='9';
            }
            return s;
        }
        if(k==2){
            for(int i=0;i<n;i++){
                s.push_back('8');
            }
            for(int i=1;i<n-1;i++){
                s[i]='9';
            }
            return s;
        }
        if(k==5){
            for(int i=0;i<n;i++){
                s.push_back('5');
            }
            for(int i=1;i<n-1;i++){
                s[i]='9';
            }
            return s;
        }
        if(k==6){
            if(n==1) return "6";
            if(n==2) return "66";
            string s1="8",s2="8";
            int sum=16,num=n-2;
            if((n%2)==0){
                while(num>2){
                    s1.push_back('9');
                    s2.push_back('9');
                    sum+=18;
                    num-=2;
                }
                for(int i=9;i>=0;i--){
                    if(((sum+2*i)%3)==0){
                        s1.push_back(i+'0');
                        s2.push_back(i+'0');
                        break;
                    }
                }
                while(s2.size()){
                    s1.push_back(s2.back());
                    s2.pop_back();
                }
                return s1;
            }
            else{
                while(num>1){
                    s1.push_back('9');
                    s2.push_back('9');
                    sum+=18;
                    num-=2;
                }
                for(int i=9;i>=0;i--){
                    if(((sum+i)%3)==0){
                        s1.push_back(i+'0');
                        break;
                    }
                }
                while(s2.size()){
                    s1.push_back(s2.back());
                    s2.pop_back();
                }
                return s1;
            }
        }
        if(n==1) return "7";
        if(n==2) return "77";
        for(int i=0;i<n;i++){
            s.push_back('9');
        }
        if((n%2)==0){
            for(int i=9;i>=0;i--){
                s[n/2]=i+'0';
                s[n/2-1]=i+'0';
                if(check7(s)){
                    return s;
                }
            }
        }
        for(int i=9;i>=0;i--){
            s[n/2]=i+'0';
            if(check7(s)){
                return s;
            }
        }
        return "0";
    }
};
