import java.math.BigInteger;

class Solution {
    public String largestPalindrome(int n, int k) {
        return sub(n, k);
    }

    private String sub(int n, int k) {
        String res = null;
        char[] check = new char[n];
        Arrays.fill(check, '9');
        if (k == 1 || k == 3 || k == 9) return new String(check);
        if (k == 2) {
            check[0] = '8';
            check[n-1] = '8';
            return new String(check);
        }
        if (k == 4) {
            if (n == 1) return "8";
            check[0] = '8';
            check[1] = '8';
            check[n-1] = '8';
            check[n-2] = '8';
            return new String(check);
        }
        if (k == 8) {
            if (n <= 6) Arrays.fill(check, '8');
            else {
                check[0] = '8';
                check[1] = '8';
                check[2] = '8';
                check[n-1] = '8';
                check[n-2] = '8';
                check[n-3] = '8';
            }
            return new String(check);
        }
        if (k == 5) {
            check[0] = '5';
            check[n-1] = '5';
            return new String(check);
        }
        int l = (n - 1) / 2, r = n/2;
        if (k == 6) {
            if (n == 1) return "6";
            if (n == 2) return "66";
            check[0] = '8';
            check[n-1] = '8';
            if (n % 2 == 1) check[l] = '8';
            else {
                check[l] = '7';
                check[r] = '7';
            }
            return new String(check);
        }

        // case 7
        BigInteger md = new BigInteger("7");
        for (char c = '9'; c >= '0'; c--) {
            check[l] = c;
            check[r] = c;
            BigInteger bi = new BigInteger(new String(check));
            if (bi.mod(md).equals(BigInteger.ZERO)) return new String(check);
        }

        return null;
    }
}
