import sys
sys.set_int_max_str_digits(200000)

class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k in (1, 3, 9):
            return '9' * n

        if k == 2:
            if n == 1:
                return '8'
            return f'8{"9" * (n - 2)}8'

        if k == 4:
            if n <= 3:
                return '8' * n
            return f'88{"9" * (n - 4)}88'

        if k == 5:
            if n == 1:
                return '5'
            return f'5{"9" * (n - 2)}5'

        if k == 6:
            if n == 1:
                return '6'
            if n == 2:
                return '66'
            if n >= 3:
                if n % 2 == 1:
                    return f'8{"9" * ((n - 3) // 2)}8{"9" * ((n - 3) // 2)}8'
                return f'8{"9" * ((n - 4) // 2)}77{"9" * ((n - 4) // 2)}8'

        if k == 7:
            if n <= 2:
                return '7' * n

            if n % 2 == 1:
                for mid in range(9, 0, -1):
                    num = int(f'{"9" * ((n - 1) // 2)}{mid}{"9" * ((n - 1) // 2)}')
                    if num % 7 == 0:
                        return str(num)

            if n % 2 == 0:
                for mid in range(9, 0, -1):
                    num = int(f'{"9" * ((n - 2) // 2)}{mid}{mid}{"9" * ((n - 2) // 2)}')
                    if num % 7 == 0:
                        return str(num)

            return str(-1)

        if k == 8:
            if n <= 5:
                return '8' * n
            return f'888{"9" * (n - 6)}888'
