class Solution {
public:

    bool isDivisibleBy7(string& s) {
        int remainder = 0;
        for (char digit : s) {
            remainder = remainder * 10 + (digit - '0');
            remainder %= 7;
        }
        return remainder == 0;
    }

    string largestPalindrome(int n, int k) {
        string ans;
        int i;

        if(k == 1 or k == 3 or k == 9){
            for(i=0;i<n;i++)    ans.push_back('9');
        }
        else if(k == 4){
            for(i=0;i<n;i++)    ans.push_back('9');
            ans[0] = '8';
            ans.back() = '8';
            if(n > 2){
                ans[1] = '8'; ans[n-2] = '8';
            }
        }
        else if(k == 8){
            for(i=0;i<n;i++)    ans.push_back('9');
            ans[0] = '8';
            ans.back() = '8';
            if(n > 2){
                ans[1] = '8'; ans[n-2] = '8';
                ans[2] = '8'; ans[n-3] = '8';
            }
        }
        else if(k == 2){
            for(i=0;i<n;i++)    ans.push_back('9');
            ans[0] = '8';
            ans.back() = '8';
        }
        else if(k == 5){
            for(i=0;i<n;i++)    ans.push_back('9');
            ans[0] = '5';
            ans.back() = '5';
        }
        else if(k == 6){
            if(n <= 2){
                ans = (n == 1 ? "6" : "66");
            }
            else{
                for(i=0;i<n;i++)    ans.push_back('9');
                ans[0] = '8';
                ans.back() = '8';
                if(n % 2)   ans[n/2] = '8';
                else{
                    ans[n/2] = '7';
                    ans[n/2-1] = '7';
                }
            }
        }
        else if(k == 7){
            if(n <= 2){
                ans = (n == 1 ? "7" : "77");
            }
            else{
                for(i=0;i<n;i++)    ans.push_back('9');
                if(n % 2){
                    for(i=9;i>=0;i--){
                        ans[n/2] = '0' + i;
                        if(isDivisibleBy7(ans)) return ans;
                    }
                    return "debug";
                }
                else{
                    for(i=9;i>=0;i--){
                        ans[n/2] = '0' + i;
                        ans[n/2-1] = '0' + i;
                        if(isDivisibleBy7(ans)) return ans;
                    }
                    return "debug";
                }
            }
        }
        else{
            return "invalid";
        }

        return ans;
    }
};
