class Solution {
public:
    bool good(string s) {
        int rem = 0;   
        int x = 0; 
        for (int i = 0; i < s.size(); i++) {
            x += (s[i] - '0'); 
            x %= 7; 
            x *= 10; 
        }
        return x == 0; 
    }

    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9)
            return string(n, '9'); 

        if (k == 5) {
            if (n == 1)
                return "5";
            return "5" + string(max(n-2, 0), '9') + "5"; 
        }
        if (k==2||k == 4||k==8) {
            int factor = 1;
            if (k == 4)
                factor = 2;
            if (k == 8)
                factor = 3; 

            string ans = string(factor, '8') + string(max(n - factor * 2, 0), '9') + string(factor, '8'); 
            while (ans.size() > n)
                ans.pop_back();
            return ans; 
        }
        if (k == 6) {
            if (n == 1)
                return "6"; 
            if (n == 2)
                return "66"; 
            if (n % 2) {
                string nines = string(max((n-3)/2, 0), '9');
                return "8" + nines + "8" + nines + "8"; 
            }
            else {
                string nines = string(max((n-4)/2, 0), '9');
                return "8" + nines + "77" + nines + "8"; 
            }
        }

        // mod 7 case 
        if (n == 1)
            return "7"; 
        if (n == 2)
            return "77"; 

        if (n % 2) {
            for (int i = 9; i >= 0; i--) {
                string ans = string(n/2, '9') + string(1, ('0' + i)) + string(n/2, '9'); 
                if (good(ans))
                    return ans; 
            }
        }
        else {
            for (int i = 9; i >= 0; i--) {
                string ans = string(n/2 - 1, '9') + string(2, ('0' + i))  + string(n/2 - 1, '9'); 
                if (good(ans))
                    return ans; 
            }
        }
        return ""; 
    }
};
