class Solution {
public:
    string largestPalindrome(int n, int k) {
        // 末尾不能是0
        int mid = n / 2;
        string ans(n, '9');
        if (k == 1 || k == 3 || k == 9) return ans;
        if (k == 2) {
            ans[0] = ans[n - 1] = '8';
            return ans;
        }
        if (k == 4) {
            if (n == 1) return "8";
            ans[0] = ans[n - 1] = '8';
            ans[1] = ans[n - 2] = '8';
            return ans;
        }
        if (k == 5) {
            ans[0] = ans[n - 1] = '5';
            return ans;
        }
        if (k == 6) {
            if (n == 1) return "6";
            if (n == 2) return "66";
            ans[0] = ans[n - 1] = '8';
            if (n % 2 == 1) {
                ans[mid] = '8';
            } 
            else {
                ans[mid] = ans[mid - 1] = '7';
            }
            return ans;
        }
        if (k == 7) {
            if (n == 1) return "7";
            if (n == 2) return "77";
            for (int i = 9; i >= 0; --i) {
                ans[mid] = ans[n - 1 - mid] = '0' + i;
                //int cur = 0;
                int flag = 0;
                for (int j = 0; j < n; ++j) {
                    int x = flag * 10 + (ans[j] - '0');
                    flag = x % 7;
                }
                if (flag == 0) {
                    return ans;
                }
            }
            return "";
        }
        if (k == 8) {
            if (n == 1) return "8";
            if (n == 2) return "88";
            ans[0] = ans[n - 1] = '8';
            ans[1] = ans[n - 2] = '8';
            ans[2] = ans[n - 3] = '8';
            return ans;
        }

        return "";
    }
};
