class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k==1 || k==3 || k==9) return string(n, '9');
        if(k==5){
            if(n<=2) return string(n, '5');
            else{
                string res(n, '9');
                res[0]='5';
                res[n-1]='5';
                return res;
            }
        }
        if(k==6){
            if(n<=2) return string(n, '6');
            else if(n==3) return string(n, '8');
            else if(n==4) return "8778";
            else{
                if(n&1){
                    string res(n, '9');
                    res[0]='8', res[n-1]='8';
                    res[n/2]='8';
                    return res;
                }else{
                    string t(n/2, '9');
                    t[0]='8'; t[t.size()-1]='7';
                    string res;
                    res+=t;
                    reverse(t.begin(), t.end());
                    res+=t;
                    return res;
                }
            }
        }
        if(k==7){
            if(n<=2) return string(n, '7');
            else {
                if(n&1){
                    int arr[6]={5, 7, 4, 6, 4, 7};
                    string res(n, '9');
                    int c=n/2;
                    res[n/2]='0'+arr[(c-1)%6];
                    return res;
                }else{
                    int arr[6]={7, 9, 4, 4, 9, 7};
                    string res(n, '9');
                    int c=n/2-1;
                    res[n/2]='0'+arr[(c-1)%6];
                    res[n/2-1]='0'+arr[(c-1)%6];
                    return res;
                }
            }
        }
        if(k==8){
            if(n<=6) return string(n, '8');
            string res(n, '9');
            for(int i=0; i<3; i++){
                res[i]='8';
                res[n-1-i]='8';
            }
            return res;
        }
        if(k==4){
            if(n<=4) return string(n, '8');
            string res(n, '9');
            for(int i=0; i<2; i++){
                res[i]='8';
                res[n-1-i]='8';
            }
            return res;
        }
        if(k==2){
            if(n<=2) return string(n, '8');
            string res(n, '9');
            for(int i=0; i<1; i++){
                res[i]='8';
                res[n-1-i]='8';
            }
            return res;
        }
        return string(1,'0');
    }
};
