class Solution {
    String build(int n, int k)
    {
        char[] c = new char[n];
        Arrays.fill(c, (char)('0' + k));
        return String.valueOf(c);
    }
    String build9(int n)
    {
        return build(n, 9);
    }
    public String largestPalindrome(int n, int k) {
