        if(k == 1) return build9(n);
        if(k == 2)
        {
            if(n <= 2) return build(n, 8);
            return "8" + build9(n - 2) + "8";
        }
        if(k == 4)
        {
            if(n <= 4) return build(n, 8);
            return "88" + build9(n - 4) + "88";
        }
        if(k == 5)
        {
            if(n <= 2) return build(n, 5);
            return "5" + build9(n - 2) + "5";
        }
        if(k == 6)
        {
            if(n <= 2) return build(n, 6);
            // if(n == 3) return build(n, 8);
            return build6(n);
        }
        if(k == 7)
        {
            if(n <= 2) return build(n, 7);
            return build7(n);
        }
        if(k == 3 || k == 9)
        {
            return build9(n);
        }
        if(k == 8)
        {
            if(n <= 6) return build(n, 8);
            return "888" + build9(n - 6) + "888";
        }


        return "";
