    String build7(int n)
    {
        char[] c = new char[n];
        Arrays.fill(c, '9');
        int i = n / 2;
        int j = n % 2 == 0 ? i - 1 : i;
        BigInteger b7 = new BigInteger("7");
        while(!new BigInteger(String.valueOf(c)).remainder(b7).equals(BigInteger.ZERO))
        {
            // System.out.println(String.valueOf(c));
            c[i] = (char)(c[i] - 1);
            if(i != j) c[j] = (char)(c[j] - 1);

        }
        return String.valueOf(c);
    }

    String build6(int n)
    {
        char[] c = new char[n];
        c[0] = '8';
        c[n - 1] = '8';
        int i = 1, j = n - 2;
        int sum = (n - 2) * 9 + 16;
        if(n % 2 == 0)
        {
            while(sum % 3 != 0 || sum % 2 != 0) sum--;
        }
        else
        {
            while(sum % 3 != 0) sum--;
        }
        // System.out.println("sum=" + sum);
        sum -= 16;
        while(i <= j)
        {
            if(i != j)
            {
                if(sum >= 2 * 9)
                {
                    sum -= 18;
                    c[i] = c[j] = '9';
                }
                else
                {
                    c[i] = c[j] = (char)('0' + sum / 2);
                }
            }
            else
            {
                c[i] = (char)('0' + sum);
            }
            i++;
            j--;
        }
        return String.valueOf(c);
    }

    boolean isPalin(int k)
    {
        char[] c = String.valueOf(k).toCharArray();
        int n = c.length;
        int i = 0, j = n - 1;
        while(i < j)
        {
            if(c[i] != c[j]) return false;
            i++;
            j--;
        }
        return true;
    }
}
