class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return '9' * n
        if k == 2 :
            return '8' * n if n <= 2 else '8' + '9' * (n - 2) + '8' 
        if k == 4:
            return '8' * n if n <= 4 else '88' + '9' * (n - 4) + '88' 
        if k == 5:
            return '5' if n == 1 else '5' + '9' * (n - 2) + '5' 
        if k == 6:
            if n <= 2:
                return '6' * n
            if n == 3:
                return '888'

            m = (n - 2) // 2
            if n & 1:
                plus = '9' * m + '8' + '9' * m
            else:
                t = max(m - 1, 0)
                plus = '9' * t + '7' * 2 + '9' * t

            return '8' + plus + '8'
        if k == 7:
            if n <= 2:
                return '7' * n

            multi = [1, 3, 2, -1, -3, -2]
            arr = ['9'] * n
            mid = [n // 2] if n & 1 else [n // 2, (n // 2) - 1]
            s = mul = 0
            for i in range(n):
                if i in mid:
                    mul += multi[i % 6]
                else:
                    s = (s + 9 * multi[i % 6]) % 7

            for d in range(9, -1, -1):
                if (s + d * mul) % 7 == 0:
                    for i in mid:
                        arr[i] = str(d)
                    break

            return ''.join(arr)

        if k == 8:
            if n <= 6:
                return '8' * n
            return '8' * 3 + '9' * (n - 6) + '8' * 3

        return 0
