class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:

        def is_divisible_by(num_str, k):
            remainder = 0
            for digit in num_str:
                remainder = (remainder * 10 + int(digit)) % k
            return remainder == 0

        def subtract_one(number_string):
            digits = list(number_string)
            i = len(digits) - 1
            while i >= 0:
                if digits[i] > '0':
                    digits[i] = chr(ord(digits[i]) - 1)
                    break
                else:
                    digits[i] = '9'
                    i -= 1

            if digits[0] == '0':
                digits.pop(0)

            return ''.join(digits)


        if k == 1 or k == 9 or k == 3:
            return '9'*n
        if k == 5:
            return '5'*n if n <= 1 else '5' + '9'*(n-2) + '5'
        if k == 2:
            return '8'*n if n <= 1 else '8' + '9'*(n-2) + '8'

        if k == 4:
            if n <= 4: return '8'*n
            else: return '88' + '9'*(n-4) + '88'

        if k == 8:
            if n <= 6: return '8'*n
            else: return '888' + '9'*(n-6) + '888'

        # k = 4,6,7,8

        if n <= 2:
            return str(k)*n

        half_n = (n - 1) // 2
        base_digits = {
            6: '8642',
            7: '97531'
        }

        for a in base_digits[k]:
            temp = '9'*((n - 1)//2)
            # end = '0'*((n - 1)//2)
            while temp != '-1':
                if n % 2 == 0:
                    palindrome = a + temp + temp[::-1] + a
                else:
                    palindrome = a + temp + temp[:-1][::-1] + a

                if is_divisible_by(palindrome, k):
                    return palindrome

                temp = subtract_one(temp)

        return '0'
